(1680, 13, 12, 'Yūsuf', 'يُوسُف', 245, 12, 84, '‏‏‏‏ ‏‏‏‏ ‏‏‏‏ ‏‏‏‏ ‏ ‏‏‏‏ ‏‏‏‏‏‏ ‏‏‏‏‏ ‏‏ ‏‏‏‏ ‏‏‏ ‏‏‏‏ ‏', 'وتولى عنهم وقال ياأسفى على يوسف وابيضت عيناه من الحزن فهو كظيم'),
(1681, 13, 12, 'Yūsuf', 'يُوسُف', 245, 14, 85, '‏‏‏‏‏ ‏‏‏ ‏‏‏‏‏ ‏‏‏‏ ‏‏‏‏ ‏‏ ‏‏‏‏ ‏‏‏‏ ‏‏ ‏‏‏‏ ‏‏ ‏‏‏‏‏‏ ‏', 'قالوا تالله تفتأ تذكر يوسف حتى تكون حرضا أو تكون من الهالكين'),
(1682, 13, 12, 'Yūsuf', 'يُوسُف', 245, 15, 86, '‏‏‏ ‏‏‏‏ ‏‏‏‏‏ ‏‏ ‏‏‏‏ ‏‏ ‏‏ ‏‏‏‏‏ ‏‏ ‏‏ ‏‏ ‏ ‏‏‏‏‏‏ ‏', 'قال إنما أشكو بثي وحزني إلى الله وأعلم من الله ما لا تعلمون'),
(1683, 13, 12, 'Yūsuf', 'يُوسُف', 246, 3, 87, '‏‏‏ ‏‏‏‏‏‏ ‏‏‏‏‏‏‏ ‏‏ ‏‏‏‏ ‏‏‏‏‏ ‏‏ ‏‏‏‏‏‏ ‏‏ ‏‏‏‏ ‏‏‏ ‏‏‏‏ ‏ ‏‏‏‏ ‏‏ ‏‏‏‏ ‏‏ ‏‏ ‏‏‏‏‏ ‏‏‏‏‏‏‏‏ ‏', 'يابني اذهبوا فتحسسوا من يوسف وأخيه ولا تيأسوا من روح الله إنه لا ييأس من روح الله إلا القوم الكافرون'),
(1684, 13, 12, 'Yūsuf', 'يُوسُف', 246, 5, 88, '‏‏‏‏ ‏‏‏‏‏ ‏‏‏‏ ‏‏‏‏‏ ‏‏‏‏‏ ‏‏‏‏‏‏ ‏‏‏‏ ‏‏‏‏‏‏ ‏‏‏‏ ‏‏‏‏‏ ‏‏‏‏‏ ‏‏‏‏‏ ‏‏‏‏ ‏‏ ‏‏‏‏‏ ‏‏‏‏‏ ‏‏‏‏‏‏ ‏‏ ‏‏ ‏‏‏ ‏‏‏‏‏‏‏‏ ‏', 'فلما دخلوا عليه قالوا ياأيها العزيز مسنا وأهلنا الضر وجئنا ببضاعة مزجاة فأوف لنا الكيل وتصدق علينا إن الله يجزي المتصدقين'),
(1685, 13, 12, 'Yūsuf', 'يُوسُف', 246, 7, 89, '‏‏‏ ‏‏ ‏‏‏‏‏ ‏‏ ‏‏‏‏‏ ‏‏‏‏‏ ‏‏‏‏‏ ‏‏ ‏‏‏‏ ‏‏‏‏‏ ‏', 'قال هل علمتم ما فعلتم بيوسف وأخيه إذ أنتم جاهلون'),
(1686, 13, 12, 'Yūsuf', 'يُوسُف', 246, 9, 90, '‏‏‏‏‏ ‏‏‏‏ ‏‏‏ ‏‏‏‏‏ ‏‏‏ ‏‏‏ ‏‏‏‏ ‏‏‏‏ ‏‏‏ ‏‏ ‏‏ ‏‏ ‏‏‏‏‏‏ ‏‏‏‏ ‏‏ ‏‏‏ ‏‏‏‏‏ ‏‏‏ ‏‏ ‏ ‏‏‏‏ ‏‏‏ ‏‏‏‏‏‏‏ ‏', 'قالوا أئنك لأنت يوسف قال أنا يوسف وهذا أخي قد من الله علينا إنه من يتق ويصبر فإن الله لا يضيع أجر المحسنين'),
(1687, 13, 12, 'Yūsuf', 'يُوسُف', 246, 10, 91, '‏‏‏‏‏ ‏‏‏ ‏‏‏ ‏‏‏‏‏ ‏‏ ‏‏‏‏‏ ‏‏ ‏‏‏ ‏‏‏‏ ‏', 'قالوا تالله لقد آثرك الله علينا وإن كنا لخاطئين'),
(1688, 13, 12, 'Yūsuf', 'يُوسُف', 246, 11, 92, '‏‏‏ ‏ ‏‏‏‏‏ ‏‏‏‏‏ ‏‏‏‏‏ ‏‏‏‏ ‏‏ ‏‏‏‏ ‏‏‏ ‏‏‏‏ ‏‏‏‏‏ ‏', 'قال لا تثريب عليكم اليوم يغفر الله لكم وهو أرحم الراحمين'),
(1689, 13, 12, 'Yūsuf', 'يُوسُف', 246, 13, 93, '‏‏‏‏‏‏ ‏‏‏‏‏ ‏‏‏ ‏‏‏‏‏‏ ‏ ‏‏‏ ‏‏ ‏‏‏ ‏‏‏‏‏ ‏‏‏‏‏ ‏‏‏‏‏‏ ‏‏‏‏ ‏', 'اذهبوا بقميصي هذا فألقوه على وجه أبي يأت بصيرا وأتوني بأهلكم أجمعين'),
(1690, 13, 12, 'Yūsuf', 'يُوسُف', 246, 15, 94, '‏‏‏‏ ‏‏‏‏ ‏‏‏‏‏ ‏‏‏ ‏‏‏‏‏ ‏‏ ‏‏‏ ‏‏‏ ‏‏‏‏‏ ‏‏‏ ‏‏ ‏‏‏‏‏‏ ‏', 'ولما فصلت العير قال أبوهم إني لأجد ريح يوسف لولا أن تفندون'),
(1691, 13, 12, 'Yūsuf', 'يُوسُف', 246, 15, 95, '‏‏‏‏‏ ‏‏‏ ‏‏‏ ‏‏ ‏‏‏‏ ‏‏‏‏‏‏ ‏', 'قالوا تالله إنك لفي ضلالك القديم'),
(1692, 13, 12, 'Yūsuf', 'يُوسُف', 247, 2, 96, '‏‏‏‏ ‏‏ ‏‏‏ ‏‏‏‏‏ ‏‏‏‏‏ ‏ ‏‏‏‏‏ ‏‏‏‏‏ ‏‏‏‏‏‏ ‏‏‏ ‏‏‏ ‏‏‏ ‏‏‏ ‏‏ ‏‏‏‏ ‏‏ ‏‏ ‏‏ ‏ ‏‏‏‏‏‏ ‏', 'فلما أن جاء البشير ألقاه على وجهه فارتد بصيرا قال ألم أقل لكم إني أعلم من الله ما لا تعلمون'),
(1693, 13, 12, 'Yūsuf', 'يُوسُف', 247, 3, 97, '‏‏‏‏‏ ‏‏‏‏‏‏ ‏‏‏‏‏‏ ‏‏ ‏‏‏‏‏‏ ‏‏‏ ‏‏‏ ‏‏‏ ‏', 'قالوا ياأبانا استغفر لنا ذنوبنا إنا كنا خاطئين'),
(1694, 13, 12, 'Yūsuf', 'يُوسُف', 247, 4, 98, '‏‏‏ ‏‏‏ ‏‏‏‏‏‏ ‏‏‏ ‏‏‏ ‏‏‏‏ ‏‏ ‏‏‏‏‏‏‏ ‏‏‏‏‏‏ ‏', 'قال سوف أستغفر لكم ربي إنه هو الغفور الرحيم'),
(1695, 13, 12, 'Yūsuf', 'يُوسُف', 247, 6, 99, '‏‏‏‏ ‏‏‏‏‏ ‏ ‏‏‏‏ ‏‏‏‏ ‏‏‏ ‏‏‏‏‏ ‏‏‏‏ ‏‏‏‏‏‏ ‏‏‏ ‏‏ ‏‏‏ ‏‏ ‏‏‏‏‏ ‏', 'فلما دخلوا على يوسف آوى إليه أبويه وقال ادخلوا مصر إن شاء الله آمنين');
