(2266, 16, 19, 'Maryam', 'مَريَم', 306, 5, 16, '‏‏‏‏‏ ‏ ‏‏‏‏‏ ‏‏‏‏ ‏‏ ‏‏‏‏‏‏ ‏‏ ‏‏‏‏‏ ‏‏‏‏ ‏‏‏‏‏ ‏', 'واذكر في الكتاب مريم إذ انتبذت من أهلها مكانا شرقيا'),
(2267, 16, 19, 'Maryam', 'مَريَم', 306, 6, 17, '‏‏‏‏‏ ‏‏ ‏‏‏‏‏ ‏‏‏‏‏ ‏‏‏‏‏‏‏ ‏‏‏‏ ‏‏‏‏‏‏ ‏‏‏‏‏ ‏‏‏ ‏‏‏‏ ‏‏‏‏ ‏', 'فاتخذت من دونهم حجابا فأرسلنا إليها روحنا فتمثل لها بشرا سويا'),
(2268, 16, 19, 'Maryam', 'مَريَم', 306, 7, 18, '‏‏‏‏ ‏‏ ‏‏‏‏ ‏‏‏‏‏ ‏‏‏ ‏‏ ‏‏‏ ‏‏‏‏ ‏', 'قالت إني أعوذ بالرحمن منك إن كنت تقيا'),
(2269, 16, 19, 'Maryam', 'مَريَم', 306, 8, 19, '‏‏‏ ‏‏‏‏ ‏‏‏ ‏‏‏‏ ‏‏‏ ‏‏‏ ‏‏ ‏‏‏‏ ‏‏‏‏ ‏', 'قال إنما أنا رسول ربك لأهب لك غلاما زكيا'),
(2270, 16, 19, 'Maryam', 'مَريَم', 306, 9, 20, '‏‏‏‏ ‏‏ ‏‏‏‏ ‏ ‏‏‏ ‏‏‏ ‏‏‏‏‏ ‏‏‏ ‏‏‏ ‏‏ ‏‏‏‏ ‏', 'قالت أنى يكون لي غلام ولم يمسسني بشر ولم أك بغيا'),
(2271, 16, 19, 'Maryam', 'مَريَم', 306, 11, 21, '‏‏‏ ‏‏‏‏ ‏‏‏ ‏‏‏ ‏‏ ‏ ‏‏‏ ‏‏‏‏‏‏‏ ‏‏‏‏ ‏‏‏‏‏ ‏‏‏‏‏ ‏‏‏‏ ‏‏‏ ‏‏‏‏ ‏‏‏‏‏ ‏', 'قال كذلك قال ربك هو علي هين ولنجعله آية للناس ورحمة منا وكان أمرا مقضيا'),
(2272, 16, 19, 'Maryam', 'مَريَم', 306, 12, 22, '‏‏‏‏‏‏‏ ‏‏‏‏‏‏‏ ‏‏‏ ‏‏‏‏ ‏‏‏‏ ‏', 'فحملته فانتبذت به مكانا قصيا'),
(2273, 16, 19, 'Maryam', 'مَريَم', 306, 13, 23, '‏‏‏‏‏‏‏ ‏‏‏‏‏‏ ‏‏ ‏‏‏ ‏‏‏‏‏ ‏‏‏‏ ‏‏‏‏‏ ‏‏ ‏‏‏ ‏‏‏ ‏‏‏‏ ‏‏‏‏ ‏‏‏‏‏ ‏', 'فأجاءها المخاض إلى جذع النخلة قالت ياليتني مت قبل هذا وكنت نسيا منسيا'),
(2274, 16, 19, 'Maryam', 'مَريَم', 306, 14, 24, '‏‏‏‏‏‏‏ ‏‏ ‏‏‏‏ ‏‏ ‏‏‏ ‏‏ ‏‏‏ ‏‏‏ ‏‏‏ ‏‏‏‏ ‏', 'فناداها من تحتها ألا تحزني قد جعل ربك تحتك سريا'),
(2275, 16, 19, 'Maryam', 'مَريَم', 306, 15, 25, '‏‏‏‏ ‏‏‏ ‏‏‏ ‏‏‏‏‏ ‏‏‏‏ ‏‏‏‏ ‏‏‏‏ ‏‏‏‏ ‏', 'وهزي إليك بجذع النخلة تساقط عليك رطبا جنيا'),
(2276, 16, 19, 'Maryam', 'مَريَم', 307, 2, 26, '‏‏ ‏‏‏‏‏ ‏‏‏‏ ‏‏‏‏‏ ‏‏‏‏ ‏‏‏‏ ‏‏ ‏‏‏‏ ‏‏‏‏ ‏‏‏‏ ‏‏ ‏‏‏‏ ‏‏‏‏ ‏‏‏‏ ‏‏‏ ‏‏‏‏ ‏‏‏‏ ‏‏‏‏‏ ‏', 'فكلي واشربي وقري عينا فإما ترين من البشر أحدا فقولي إني نذرت للرحمن صوما فلن أكلم اليوم إنسيا'),
(2277, 16, 19, 'Maryam', 'مَريَم', 307, 3, 27, '‏‏‏‏ ‏‏‏ ‏‏‏‏‏ ‏‏‏‏‏‏ ‏‏‏‏‏ ‏‏‏‏‏ ‏‏‏ ‏‏‏ ‏‏‏ ‏‏‏‏ ‏', 'فأتت به قومها تحمله قالوا يامريم لقد جئت شيئا فريا'),
(2278, 16, 19, 'Maryam', 'مَريَم', 307, 5, 28, '‏‏‏‏ ‏‏‏‏‏ ‏‏ ‏‏ ‏‏‏‏ ‏‏‏‏ ‏‏‏ ‏‏‏ ‏‏‏ ‏‏‏ ‏‏‏‏ ‏', 'ياأخت هارون ما كان أبوك امرأ سوء وما كانت أمك بغيا'),
(2279, 16, 19, 'Maryam', 'مَريَم', 307, 6, 29, '‏‏‏‏‏‏ ‏‏‏‏ ‏‏‏‏‏ ‏‏‏ ‏‏‏ ‏‏ ‏‏ ‏ ‏‏‏‏‏ ‏‏‏‏ ‏', 'فأشارت إليه قالوا كيف نكلم من كان في المهد صبيا'),
(2280, 16, 19, 'Maryam', 'مَريَم', 307, 7, 30, '‏‏‏ ‏‏ ‏‏‏ ‏‏ ‏‏‏‏‏ ‏‏‏‏‏ ‏‏‏‏‏ ‏‏‏‏ ‏', 'قال إني عبد الله آتاني الكتاب وجعلني نبيا'),
(2281, 16, 19, 'Maryam', 'مَريَم', 307, 8, 31, '‏‏‏‏‏ ‏‏‏‏‏ ‏‏‏ ‏‏ ‏‏‏ ‏‏‏‏‏ ‏‏‏‏‏‏‏ ‏‏‏‏‏‏‏ ‏‏ ‏‏‏ ‏‏‏ ‏', 'وجعلني مباركا أين ما كنت ‎وأوصاني بالصلاة والزكاة ما دمت حيا'),
(2282, 16, 19, 'Maryam', 'مَريَم', 307, 9, 32, '‏‏‏‏ ‏‏‏‏ ‏‏‏ ‏‏‏‏ ‏‏‏‏‏ ‏‏‏‏ ‏', 'وبرا بوالدتي ولم يجعلني جبارا شقيا'),
(2283, 16, 19, 'Maryam', 'مَريَم', 307, 10, 33, '‏‏‏‏‏‏ ‏ ‏‏‏ ‏‏‏ ‏‏‏‏ ‏‏‏‏ ‏‏‏‏ ‏‏‏‏ ‏‏‏ ‏', 'والسلام علي يوم ولدت ويوم أموت ويوم أبعث حيا'),
(2284, 16, 19, 'Maryam', 'مَريَم', 307, 11, 34, '‏‏‏ ‏‏‏ ‏‏‏ ‏‏‏‏‏ ‏‏‏ ‏‏‏ ‏‏‏ ‏‏‏ ‏‏‏‏‏‏‏ ‏', 'ذلك عيسى ابن مريم قول الحق الذي فيه يمترون'),
(2285, 16, 19, 'Maryam', 'مَريَم', 307, 12, 35, '‏‏ ‏‏ ‏ ‏‏ ‏‏‏‏ ‏‏ ‏‏‏ ‏‏‏‏‏‏‏ ‏‏‏ ‏‏ ‏‏‏‏ ‏‏‏‏‏ ‏‏‏‏ ‏‏ ‏‏ ‏‏‏‏‏ ‏', 'ما كان لله أن يتخذ من ولد سبحانه إذا قضى أمرا فإنما يقول له كن فيكون'),
(2286, 16, 19, 'Maryam', 'مَريَم', 307, 13, 36, '‏‏ ‏‏ ‏‏ ‏‏‏‏‏‏ ‏‏‏‏‏‏‏‏ ‏‏‏ ‏‏‏ ‏‏‏‏‏‏ ‏', 'وإن الله ربي وربكم فاعبدوه هذا صراط مستقيم'),
(2287, 16, 19, 'Maryam', 'مَريَم', 307, 14, 37, '‏‏‏‏‏‏ ‏‏‏‏‏‏ ‏‏ ‏‏‏‏‏‏ ‏‏‏‏ ‏‏‏‏ ‏‏‏‏‏‏ ‏‏ ‏‏‏‏ ‏‏‏ ‏‏‏‏ ‏', 'فاختلف الأحزاب من بينهم فويل للذين كفروا من مشهد يوم عظيم'),
(2288, 16, 19, 'Maryam', 'مَريَم', 307, 15, 38, '‏‏‏‏ ‏‏‏ ‏‏‏‏‏ ‏‏‏ ‏‏‏‏‏‏‏ ‏‏‏ ‏‏‏‏‏‏‏ ‏‏‏‏ ‏ ‏‏‏ ‏‏‏ ‏', 'أسمع بهم وأبصر يوم يأتوننا لكن الظالمون اليوم في ضلال مبين'),
(2289, 16, 19, 'Maryam', 'مَريَم', 308, 2, 39, '‏‏‏‏‏‏‏ ‏‏‏ ‏‏‏‏‏ ‏‏ ‏‏ ‏‏‏‏ ‏‏‏ ‏ ‏‏‏‏ ‏‏‏ ‏ ‏‏‏‏‏‏ ‏', 'وأنذرهم يوم الحسرة إذ قضي الأمر وهم في غفلة وهم لا يؤمنون'),
(2290, 16, 19, 'Maryam', 'مَريَم', 308, 2, 40, '‏‏‏ ‏‏ ‏‏‏ ‏‏‏‏ ‏‏‏ ‏‏‏‏‏ ‏‏‏‏ ‏‏‏‏‏‏ ‏', 'إنا نحن نرث الأرض ومن عليها وإلينا يرجعون'),
(2291, 16, 19, 'Maryam', 'مَريَم', 308, 3, 41, '‏‏‏‏‏ ‏ ‏‏‏‏‏ ‏‏‏‏‏‏‏ ‏‏‏‏ ‏‏ ‏‏‏‏‏ ‏‏‏‏ ‏', 'واذكر في الكتاب إبراهيم إنه كان صديقا نبيا'),
(2292, 16, 19, 'Maryam', 'مَريَم', 308, 4, 42, '‏‏ ‏‏‏ ‏‏‏‏ ‏‏‏‏ ‏‏ ‏‏‏‏ ‏‏ ‏ ‏‏‏‏ ‏‏ ‏‏‏‏ ‏‏ ‏‏‏ ‏‏‏ ‏‏‏ ‏', 'إذ قال لأبيه ياأبت لم تعبد ما لا يسمع ولا يبصر ولا يغني عنك شيئا'),
(2293, 16, 19, 'Maryam', 'مَريَم', 308, 6, 43, '‏‏‏‏ ‏‏ ‏‏ ‏‏‏‏ ‏‏ ‏‏‏‏‏ ‏‏ ‏‏ ‏‏‏‏ ‏‏‏‏‏‏ ‏‏‏‏ ‏‏‏‏ ‏‏‏‏ ‏', 'ياأبت إني قد جاءني من العلم ما لم يأتك فاتبعني أهدك صراطا سويا'),
(2294, 16, 19, 'Maryam', 'مَريَم', 308, 7, 44, '‏‏‏‏ ‏ ‏‏‏‏ ‏‏‏‏‏‏‏ ‏‏ ‏‏‏‏‏‏ ‏‏ ‏‏‏‏ ‏‏‏‏ ‏', 'ياأبت لا تعبد الشيطان إن الشيطان كان للرحمن عصيا'),
(2295, 16, 19, 'Maryam', 'مَريَم', 308, 8, 45, '‏‏‏‏ ‏‏ ‏‏‏‏ ‏‏ ‏‏‏‏ ‏‏‏‏ ‏‏ ‏‏‏‏ ‏‏‏‏‏ ‏‏‏‏‏‏ ‏‏‏ ‏', 'ياأبت إني أخاف أن يمسك عذاب من الرحمن فتكون للشيطان وليا'),
(2296, 16, 19, 'Maryam', 'مَريَم', 308, 9, 46, '‏‏‏ ‏‏‏‏‏ ‏‏‏ ‏‏ ‏‏‏‏‏ ‏‏‏‏‏‏‏‏ ‏‏ ‏‏ ‏‏‏‏ ‏‏‏‏‏‏ ‏‏‏‏‏‏ ‏‏‏‏ ‏', 'قال أراغب أنت عن آلهتي ياإبراهيم لئن لم تنته لأرجمنك واهجرني مليا'),
(2297, 16, 19, 'Maryam', 'مَريَم', 308, 10, 47, '‏‏‏ ‏‏‏ ‏‏‏‏‏ ‏‏‏‏‏‏‏ ‏‏ ‏‏‏ ‏‏‏‏ ‏‏ ‏ ‏‏‏‏ ‏', 'قال سلام عليك سأستغفر لك ربي إنه كان بي حفيا'),
(2298, 16, 19, 'Maryam', 'مَريَم', 308, 12, 48, '‏‏‏‏‏‏‏‏ ‏‏‏ ‏‏‏‏‏ ‏‏ ‏‏‏ ‏‏ ‏‏‏‏‏‏ ‏‏ ‏‏ ‏‏ ‏‏‏‏ ‏‏‏‏ ‏‏ ‏‏‏‏ ‏', 'وأعتزلكم وما تدعون من دون الله وأدعو ربي عسى ألا أكون بدعاء ربي شقيا'),
(2299, 16, 19, 'Maryam', 'مَريَم', 308, 13, 49, '‏‏‏‏ ‏‏‏‏‏‏‏ ‏‏‏ ‏‏‏‏‏‏ ‏‏ ‏‏‏ ‏‏ ‏‏‏‏‏ ‏‏ ‏‏‏‏ ‏‏‏‏‏‏‏ ‏‏ ‏‏‏‏‏ ‏‏‏‏ ‏', 'فلما اعتزلهم وما يعبدون من دون الله وهبنا له إسحاق ويعقوب وكلا جعلنا نبيا'),
(2300, 16, 19, 'Maryam', 'مَريَم', 308, 14, 50, '‏‏‏‏‏‏‏ ‏‏‏ ‏‏ ‏‏‏‏‏ ‏‏‏‏‏‏ ‏‏‏ ‏‏‏‏ ‏‏‏ ‏‏‏‏ ‏', 'ووهبنا لهم من رحمتنا وجعلنا لهم لسان صدق عليا'),
(2301, 16, 19, 'Maryam', 'مَريَم', 308, 15, 51, '‏‏‏‏‏ ‏ ‏‏‏‏‏ ‏‏‏‏ ‏‏‏‏ ‏‏ ‏‏‏‏ ‏‏‏ ‏‏‏‏ ‏‏‏‏ ‏', 'واذكر في الكتاب موسى إنه كان مخلصا وكان رسولا نبيا'),
(2302, 16, 19, 'Maryam', 'مَريَم', 309, 1, 52, '‏‏‏‏‏‏ ‏‏ ‏‏‏‏ ‏‏‏‏‏‏ ‏‏‏‏‏ ‏‏‏‏‏‏ ‏‏‏ ‏', 'وناديناه من جانب الطور الأيمن وقربناه نجيا'),
(2303, 16, 19, 'Maryam', 'مَريَم', 309, 2, 53, '‏‏‏‏‏‏‏ ‏‏ ‏‏ ‏‏‏‏‏ ‏‏‏‏ ‏‏‏‏‏ ‏‏‏‏ ‏', 'ووهبنا له من رحمتنا أخاه هارون نبيا'),
(2304, 16, 19, 'Maryam', 'مَريَم', 309, 3, 54, '‏‏‏‏‏ ‏ ‏‏‏‏‏ ‏‏‏‏‏‏‏ ‏‏‏‏ ‏‏ ‏‏‏‏ ‏‏‏‏‏ ‏‏‏ ‏‏‏‏ ‏‏‏‏ ‏', 'واذكر في الكتاب إسماعيل إنه كان صادق الوعد وكان رسولا نبيا'),
(2305, 16, 19, 'Maryam', 'مَريَم', 309, 4, 55, '‏‏‏ ‏‏‏‏ ‏‏‏‏‏ ‏‏‏‏‏‏‏ ‏‏‏‏‏‏‏ ‏‏‏ ‏‏‏ ‏‏‏‏ ‏‏‏‏‏ ‏', 'وكان يأمر أهله بالصلاة والزكاة وكان عند ربه مرضيا'),
(2306, 16, 19, 'Maryam', 'مَريَم', 309, 5, 56, '‏‏‏‏‏ ‏ ‏‏‏‏‏ ‏‏‏‏‏‏ ‏‏‏‏ ‏‏ ‏‏‏‏‏ ‏‏‏‏ ‏', 'واذكر في الكتاب إدريس إنه كان صديقا نبيا'),
(2307, 16, 19, 'Maryam', 'مَريَم', 309, 5, 57, '‏‏‏‏‏‏‏ ‏‏‏‏ ‏‏‏‏ ‏', 'ورفعناه مكانا عليا');
