(3747, 23, 36, 'Yā-Sīn', 'يسٓ', 443, 2, 42, '‏‏‏‏‏‏ ‏‏‏ ‏‏ ‏‏‏‏‏ ‏‏ ‏‏‏‏‏‏ ‏', 'وخلقنا لهم من مثله ما يركبون'),
(3748, 23, 36, 'Yā-Sīn', 'يسٓ', 443, 3, 43, '‏‏ ‏‏‏ ‏‏‏‏‏‏ ‏‏ ‏‏‏‏ ‏‏‏ ‏‏ ‏‏ ‏‏‏‏‏‏ ‏', 'وإن نشأ نغرقهم فلا صريخ لهم ولا هم ينقذون'),
(3749, 23, 36, 'Yā-Sīn', 'يسٓ', 443, 3, 44, '‏‏ ‏‏‏ ‏‏‏ ‏‏‏‏‏ ‏‏ ‏‏ ‏', 'إلا رحمة منا ومتاعا إلى حين'),
(3750, 23, 36, 'Yā-Sīn', 'يسٓ', 443, 4, 45, '‏‏‏ ‏‏‏ ‏‏‏ ‏‏‏‏‏ ‏‏ ‏‏ ‏‏‏‏‏‏ ‏‏‏ ‏‏‏‏‏ ‏‏‏‏‏ ‏‏‏‏‏ ‏', 'وإذا قيل لهم اتقوا ما بين أيديكم وما خلفكم لعلكم ترحمون'),
(3751, 23, 36, 'Yā-Sīn', 'يسٓ', 443, 6, 46, '‏‏‏ ‏‏‏‏‏‏ ‏‏ ‏‏‏‏ ‏‏ ‏‏‏‏ ‏‏‏‏ ‏‏ ‏‏‏‏ ‏‏‏‏ ‏‏‏‏‏ ‏', 'وما تأتيهم من آية من آيات ربهم إلا كانوا عنها معرضين'),
(3752, 23, 36, 'Yā-Sīn', 'يسٓ', 443, 8, 47, '‏‏‏ ‏‏‏ ‏‏‏ ‏‏‏‏‏‏ ‏‏‏ ‏‏‏‏‏‏ ‏‏ ‏‏‏ ‏‏‏‏ ‏‏‏‏‏‏ ‏‏‏‏ ‏‏‏‏‏‏ ‏‏‏‏‏ ‏‏ ‏‏ ‏‏‏‏ ‏‏ ‏‏‏‏‏‏ ‏‏ ‏‏‏‏ ‏‏ ‏ ‏‏‏ ‏‏‏ ‏', 'وإذا قيل لهم أنفقوا مما رزقكم الله قال الذين كفروا للذين آمنوا أنطعم من لو يشاء الله أطعمه إن أنتم إلا في ضلال مبين'),
(3753, 23, 36, 'Yā-Sīn', 'يسٓ', 443, 9, 48, '‏‏‏‏‏‏‏ ‏‏ ‏‏‏ ‏‏‏‏‏ ‏‏ ‏‏‏‏ ‏‏‏‏ ‏', 'ويقولون متى هذا الوعد إن كنتم صادقين'),
(3754, 23, 36, 'Yā-Sīn', 'يسٓ', 443, 10, 49, '‏‏ ‏‏‏‏‏‏‏ ‏‏ ‏‏‏‏ ‏‏‏‏ ‏‏‏‏‏‏ ‏‏‏ ‏‏‏‏‏ ‏', 'ما ينظرون إلا صيحة واحدة تأخذهم وهم يخصمون'),
(3755, 23, 36, 'Yā-Sīn', 'يسٓ', 443, 11, 50, '‏‏ ‏‏‏‏‏‏‏‏ ‏‏‏‏‏ ‏‏ ‏‏ ‏‏‏‏‏ ‏‏‏‏‏‏ ‏', 'فلا يستطيعون توصية ولا إلى أهلهم يرجعون'),
(3756, 23, 36, 'Yā-Sīn', 'يسٓ', 443, 12, 51, '‏‏‏‏ ‏ ‏‏‏‏‏‏ ‏‏‏‏ ‏‏ ‏‏ ‏‏‏‏‏‏ ‏‏ ‏‏‏‏ ‏‏‏‏‏‏ ‏', 'ونفخ في الصور فإذا هم من الأجداث إلى ربهم ينسلون'),
(3757, 23, 36, 'Yā-Sīn', 'يسٓ', 443, 13, 52, '‏‏‏‏‏ ‏‏‏‏‏‏ ‏‏ ‏‏‏‏‏ ‏‏ ‏‏‏‏‏‏ ‏‏‏ ‏‏ ‏‏‏ ‏‏‏‏‏ ‏‏‏‏ ‏‏‏‏‏‏‏‏ ‏', 'قالوا ياويلنا من بعثنا من مرقدنا هذا ما وعد الرحمن وصدق المرسلون'),
(3758, 23, 36, 'Yā-Sīn', 'يسٓ', 443, 14, 53, '‏‏ ‏‏‏ ‏‏ ‏‏‏‏ ‏‏‏‏ ‏‏‏‏ ‏‏ ‏‏‏ ‏‏‏‏ ‏‏‏‏‏‏ ‏', 'إن كانت إلا صيحة واحدة فإذا هم جميع لدينا محضرون'),
(3759, 23, 36, 'Yā-Sīn', 'يسٓ', 443, 15, 54, '‏‏‏‏‏ ‏ ‏‏‏‏ ‏‏‏ ‏‏‏ ‏‏ ‏‏‏‏‏ ‏‏ ‏‏ ‏‏‏‏ ‏‏‏‏‏‏ ‏', 'فاليوم لا تظلم نفس شيئا ولا تجزون إلا ما كنتم تعملون'),
(3760, 23, 36, 'Yā-Sīn', 'يسٓ', 444, 1, 55, '‏‏ ‏‏‏‏ ‏‏‏‏ ‏‏‏‏ ‏ ‏‏‏ ‏‏‏‏‏ ‏', 'إن أصحاب الجنة اليوم في شغل فاكهون'),
(3761, 23, 36, 'Yā-Sīn', 'يسٓ', 444, 2, 56, '‏‏ ‏‏‏‏‏‏‏‏ ‏ ‏‏‏ ‏ ‏‏‏‏‏‏ ‏‏‏‏‏ ‏', 'هم وأزواجهم في ظلال على الأرائك متكئون'),
(3762, 23, 36, 'Yā-Sīn', 'يسٓ', 444, 3, 57, '‏‏‏ ‏‏‏‏ ‏‏‏‏ ‏‏‏‏ ‏‏ ‏‏‏‏‏ ‏', 'لهم فيها فاكهة ولهم ما يدعون'),
(3763, 23, 36, 'Yā-Sīn', 'يسٓ', 444, 3, 58, '‏‏‏ ‏‏‏ ‏‏ ‏‏ ‏‏‏‏ ‏', 'سلام قولا من رب رحيم'),
(3764, 23, 36, 'Yā-Sīn', 'يسٓ', 444, 4, 59, '‏‏‏‏‏‏‏‏ ‏‏‏‏ ‏‏‏‏ ‏‏‏‏‏‏‏‏ ‏', 'وامتازوا اليوم أيها المجرمون'),
(3765, 23, 36, 'Yā-Sīn', 'يسٓ', 444, 5, 60, '‏‏‏‏ ‏‏‏‏ ‏‏‏‏ ‏‏‏ ‏‏‏‏ ‏‏ ‏ ‏‏‏‏‏‏ ‏‏‏‏‏‏‏ ‏‏‏‏ ‏‏‏ ‏‏‏ ‏‏‏ ‏', 'ألم أعهد إليكم يابني آدم أن لا تعبدوا الشيطان إنه لكم عدو مبين'),
