(3776, 23, 36, 'Yā-Sīn', 'يسٓ', 445, 2, 71, '‏‏‏‏ ‏‏‏‏‏ ‏‏‏ ‏‏‏‏‏ ‏‏‏ ‏‏‏ ‏‏‏‏ ‏‏‏‏‏‏ ‏‏‏‏‏ ‏‏‏ ‏‏‏ ‏‏‏‏‏ ‏', 'أو لم يروا أنا خلقنا لهم مما عملت أيدينا أنعاما فهم لها مالكون'),
(3777, 23, 36, 'Yā-Sīn', 'يسٓ', 445, 3, 72, '‏‏‏‏‏‏‏ ‏‏‏ ‏‏‏‏‏ ‏‏‏‏‏‏ ‏‏‏‏‏ ‏‏‏‏‏‏ ‏', 'وذللناها لهم فمنها ركوبهم ومنها يأكلون'),
(3778, 23, 36, 'Yā-Sīn', 'يسٓ', 445, 4, 73, '‏‏‏‏ ‏‏‏‏ ‏‏‏‏ ‏‏‏‏‏‏‏ ‏‏‏ ‏‏‏‏‏‏‏ ‏', 'ولهم فيها منافع ومشارب أفلا يشكرون'),
(3779, 23, 36, 'Yā-Sīn', 'يسٓ', 445, 5, 74, '‏‏‏‏‏‏ ‏‏ ‏‏‏ ‏‏ ‏‏‏‏‏ ‏‏‏‏‏ ‏‏‏‏‏‏‏ ‏', 'واتخذوا من دون الله آلهة لعلهم ينصرون'),
(3780, 23, 36, 'Yā-Sīn', 'يسٓ', 445, 6, 75, '‏ ‏‏‏‏‏‏‏‏ ‏‏‏‏‏ ‏‏‏ ‏‏‏ ‏‏‏ ‏‏‏‏‏‏ ‏', 'لا يستطيعون نصرهم وهم لهم جند محضرون'),
(3781, 23, 36, 'Yā-Sīn', 'يسٓ', 445, 7, 76, '‏‏ ‏‏‏‏ ‏‏‏‏‏‏ ‏‏‏ ‏‏‏‏ ‏‏ ‏‏‏‏‏‏ ‏‏‏ ‏‏‏‏‏‏ ‏', 'فلا يحزنك قولهم إنا نعلم ما يسرون وما يعلنون'),
(3782, 23, 36, 'Yā-Sīn', 'يسٓ', 445, 8, 77, '‏‏‏‏ ‏‏ ‏‏‏‏‏ ‏‏‏ ‏‏‏‏‏ ‏‏ ‏‏‏‏ ‏‏‏‏ ‏‏ ‏‏‏‏ ‏‏‏ ‏', 'أو لم ير الإنسان أنا خلقناه من نطفة فإذا هو خصيم مبين'),
(3783, 23, 36, 'Yā-Sīn', 'يسٓ', 445, 9, 78, '‏‏‏‏ ‏‏ ‏‏‏ ‏‏‏ ‏‏‏‏‏‏ ‏‏‏ ‏‏ ‏‏ ‏‏‏‏‏ ‏‏ ‏‏‏‏ ‏', 'وضرب لنا مثلا ونسي خلقه قال من يحيي العظام وهي رميم'),
(3784, 23, 36, 'Yā-Sīn', 'يسٓ', 445, 10, 79, '‏‏ ‏‏‏‏‏ ‏‏‏ ‏‏‏‏‏‏ ‏‏‏ ‏‏‏‏ ‏‏‏ ‏‏ ‏‏‏ ‏‏‏‏ ‏', 'قل يحييها الذي أنشأها أول مرة وهو بكل خلق عليم'),
(3785, 23, 36, 'Yā-Sīn', 'يسٓ', 445, 12, 80, '‏‏‏ ‏‏‏ ‏‏‏ ‏‏ ‏‏‏‏‏ ‏‏‏‏‏ ‏‏‏‏ ‏‏‏‏ ‏‏‏‏ ‏‏‏ ‏‏‏‏‏‏ ‏', 'الذي جعل لكم من الشجر الأخضر نارا فإذا أنتم منه توقدون'),
(3786, 23, 36, 'Yā-Sīn', 'يسٓ', 445, 13, 81, '‏‏‏‏ ‏‏‏ ‏‏‏ ‏‏‏‏‏‏ ‏‏‏‏‏ ‏‏‏‏ ‏ ‏‏ ‏‏‏ ‏‏‏‏‏‏ ‏‏ ‏‏‏ ‏‏‏‏ ‏‏‏‏‏‏ ‏', 'أو ليس الذي خلق السموات والأرض بقادر على أن يخلق مثلهم بلى وهو الخلاق العليم'),
(3787, 23, 36, 'Yā-Sīn', 'يسٓ', 445, 14, 82, '‏‏‏‏ ‏‏‏‏‏ ‏‏‏ ‏‏‏‏ ‏‏‏ ‏‏ ‏‏‏‏ ‏‏ ‏‏ ‏‏‏‏‏ ‏', 'إنما أمره إذا أراد شيئا أن يقول له كن فيكون'),
(3788, 23, 36, 'Yā-Sīn', 'يسٓ', 445, 15, 83, '‏‏‏‏‏ ‏‏‏ ‏‏‏‏‏ ‏‏‏‏‏ ‏ ‏‏ ‏‏‏ ‏‏‏‏‏‏ ‏', 'فسبحان الذي بيده ملكوت كل شيء وإليه ترجعون'),
(3789, 23, 37, 'Aṣ-Ṣāffāt', 'الصَّافَات', 446, 3, 1, '‏‏‏‏‏‏ ‏‏‏ ‏', 'والصافات صفا'),
(3790, 23, 37, 'Aṣ-Ṣāffāt', 'الصَّافَات', 446, 3, 2, '‏‏‏‏‏‏‏ ‏‏‏‏ ‏', 'فالزاجرات زجرا'),
(3791, 23, 37, 'Aṣ-Ṣāffāt', 'الصَّافَات', 446, 3, 3, '‏‏‏‏‏‏‏ ‏‏‏‏ ‏', 'فالتاليات ذكرا'),
(3792, 23, 37, 'Aṣ-Ṣāffāt', 'الصَّافَات', 446, 4, 4, '‏‏ ‏‏‏‏‏ ‏‏‏‏ ‏', 'إن إلهكم لواحد'),
(3793, 23, 37, 'Aṣ-Ṣāffāt', 'الصَّافَات', 446, 4, 5, '‏‏ ‏‏‏‏‏‏ ‏‏‏‏‏ ‏‏‏ ‏‏‏‏‏‏ ‏‏‏‏ ‏‏‏‏‏‏ ‏', 'رب السموات والأرض وما بينهما ورب المشارق'),
(3794, 23, 37, 'Aṣ-Ṣāffāt', 'الصَّافَات', 446, 5, 6, '‏‏‏ ‏‏‏‏ ‏‏‏‏‏‏ ‏‏‏‏‏ ‏‏‏‏‏ ‏‏‏‏‏‏‏ ‏', 'إنا زينا السماء الدنيا بزينة الكواكب'),
(3795, 23, 37, 'Aṣ-Ṣāffāt', 'الصَّافَات', 446, 5, 7, '‏‏‏‏‏ ‏‏ ‏ ‏‏‏‏ ‏‏‏‏ ‏', 'وحفظا من كل شيطان مارد'),
(3796, 23, 37, 'Aṣ-Ṣāffāt', 'الصَّافَات', 446, 6, 8, '‏ ‏‏‏‏‏‏ ‏‏ ‏‏‏‏ ‏‏‏ ‏‏‏‏‏‏‏ ‏‏ ‏ ‏‏‏‏ ‏', 'لا يسمعون إلى الملإ الأعلى ويقذفون من كل جانب'),
(3797, 23, 37, 'Aṣ-Ṣāffāt', 'الصَّافَات', 446, 7, 9, '‏‏‏‏‏‏‏ ‏‏‏‏ ‏‏‏‏ ‏‏‏‏ ‏', 'دحورا ولهم عذاب واصب'),
(3798, 23, 37, 'Aṣ-Ṣāffāt', 'الصَّافَات', 446, 7, 10, '‏‏ ‏‏ ‏‏‏ ‏‏‏‏‏ ‏‏‏‏‏‏‏ ‏‏‏‏ ‏‏‏‏ ‏', 'إلا من خطف الخطفة فأتبعه شهاب ثاقب'),
(3799, 23, 37, 'Aṣ-Ṣāffāt', 'الصَّافَات', 446, 8, 11, '‏‏‏‏‏‏‏‏ ‏‏‏ ‏‏‏ ‏‏‏‏ ‏‏ ‏‏ ‏‏‏‏‏‏ ‏‏‏ ‏‏‏‏‏‏ ‏‏ ‏‏ ‏‏‏ ‏', 'فاستفتهم أهم أشد خلقا أم من خلقنا إنا خلقناهم من طين لازب'),
(3800, 23, 37, 'Aṣ-Ṣāffāt', 'الصَّافَات', 446, 9, 12, '‏‏ ‏‏‏‏ ‏‏‏‏‏‏‏‏ ‏', 'بل عجبت ويسخرون'),
(3801, 23, 37, 'Aṣ-Ṣāffāt', 'الصَّافَات', 446, 9, 13, '‏‏‏ ‏‏‏‏‏‏ ‏ ‏‏‏‏‏‏‏ ‏', 'وإذا ذكروا لا يذكرون'),
(3802, 23, 37, 'Aṣ-Ṣāffāt', 'الصَّافَات', 446, 10, 14, '‏‏‏ ‏‏‏‏ ‏‏‏‏ ‏‏‏‏‏‏‏‏‏ ‏', 'وإذا رأوا آية يستسخرون'),
(3803, 23, 37, 'Aṣ-Ṣāffāt', 'الصَّافَات', 446, 10, 15, '‏‏‏‏‏‏ ‏‏ ‏‏‏ ‏‏ ‏‏‏ ‏‏‏ ‏', 'وقالوا إن هذا إلا سحر مبين'),
(3804, 23, 37, 'Aṣ-Ṣāffāt', 'الصَّافَات', 446, 11, 16, '‏‏‏‏ ‏‏‏‏ ‏‏‏‏ ‏‏‏‏‏ ‏‏‏‏‏ ‏‏‏‏ ‏‏‏‏‏‏‏‏ ‏', 'أئذا متنا وكنا ترابا وعظاما أئنا لمبعوثون'),
(3805, 23, 37, 'Aṣ-Ṣāffāt', 'الصَّافَات', 446, 11, 17, '‏‏‏‏‏‏‏‏‏ ‏‏‏‏‏‏ ‏', 'أو آباؤنا الأولون'),
(3806, 23, 37, 'Aṣ-Ṣāffāt', 'الصَّافَات', 446, 12, 18, '‏‏ ‏‏‏ ‏‏‏‏‏ ‏‏‏‏‏‏ ‏', 'قل نعم وأنتم داخرون'),
(3807, 23, 37, 'Aṣ-Ṣāffāt', 'الصَّافَات', 446, 12, 19, '‏‏‏‏‏ ‏ ‏‏‏‏ ‏‏‏‏ ‏‏‏‏ ‏‏ ‏‏‏‏‏‏‏ ‏', 'فإنما هي زجرة واحدة فإذا هم ينظرون'),
(3808, 23, 37, 'Aṣ-Ṣāffāt', 'الصَّافَات', 446, 13, 20, '‏‏‏‏‏‏ ‏‏‏‏‏‏ ‏‏‏ ‏‏‏ ‏‏‏‏ ‏', 'وقالوا ياويلنا هذا يوم الدين'),
(3809, 23, 37, 'Aṣ-Ṣāffāt', 'الصَّافَات', 446, 13, 21, '‏‏‏ ‏‏‏ ‏‏‏‏‏ ‏‏‏ ‏‏‏‏ ‏‏‏ ‏‏‏‏‏‏ ‏', 'هذا يوم الفصل الذي كنتم به تكذبون'),
(3810, 23, 37, 'Aṣ-Ṣāffāt', 'الصَّافَات', 446, 14, 22, '‏‏‏‏‏‏‏‏ ‏‏‏‏ ‏‏‏‏‏ ‏‏‏‏‏‏‏‏ ‏‏‏ ‏‏‏‏ ‏‏‏‏‏‏ ‏', 'احشروا الذين ظلموا وأزواجهم وما كانوا يعبدون'),
(3811, 23, 37, 'Aṣ-Ṣāffāt', 'الصَّافَات', 446, 15, 23, '‏‏ ‏‏‏ ‏‏ ‏‏‏‏‏‏‏ ‏‏ ‏‏‏ ‏‏‏‏‏ ‏', 'من دون الله فاهدوهم إلى صراط الجحيم'),
(3812, 23, 37, 'Aṣ-Ṣāffāt', 'الصَّافَات', 446, 15, 24, '‏‏‏‏‏‏‏ ‏‏‏‏ ‏‏‏‏‏‏ ‏', 'وقفوهم إنهم مسئولون'),
(3813, 23, 37, 'Aṣ-Ṣāffāt', 'الصَّافَات', 447, 1, 25, '‏‏ ‏‏‏ ‏ ‏‏‏‏‏‏‏‏ ‏', 'ما لكم لا تناصرون'),
(3814, 23, 37, 'Aṣ-Ṣāffāt', 'الصَّافَات', 447, 1, 26, '‏‏ ‏‏ ‏‏‏‏ ‏‏‏‏‏‏‏‏ ‏', 'بل هم اليوم مستسلمون'),
(3815, 23, 37, 'Aṣ-Ṣāffāt', 'الصَّافَات', 447, 2, 27, '‏‏‏‏‏ ‏‏‏‏‏ ‏ ‏‏‏ ‏‏‏‏‏‏‏‏ ‏', 'وأقبل بعضهم على بعض يتساءلون'),
(3816, 23, 37, 'Aṣ-Ṣāffāt', 'الصَّافَات', 447, 2, 28, '‏‏‏‏‏ ‏‏‏‏ ‏‏‏‏ ‏‏‏‏‏‏‏ ‏‏ ‏‏‏‏ ‏', 'قالوا إنكم كنتم تأتوننا عن اليمين'),
(3817, 23, 37, 'Aṣ-Ṣāffāt', 'الصَّافَات', 447, 3, 29, '‏‏‏‏‏ ‏‏ ‏‏ ‏‏‏‏‏‏ ‏‏‏‏‏ ‏', 'قالوا بل لم تكونوا مؤمنين'),
(3818, 23, 37, 'Aṣ-Ṣāffāt', 'الصَّافَات', 447, 4, 30, '‏‏‏ ‏‏ ‏‏ ‏‏‏‏‏ ‏‏ ‏‏‏‏‏ ‏‏ ‏‏‏‏ ‏‏‏‏ ‏‏‏ ‏', 'وما كان لنا عليكم من سلطان بل كنتم قوما طاغين'),
(3819, 23, 37, 'Aṣ-Ṣāffāt', 'الصَّافَات', 447, 4, 31, '‏‏‏ ‏‏‏‏‏ ‏‏‏ ‏‏‏‏‏ ‏‏‏ ‏‏‏‏‏‏ ‏', 'فحق علينا قول ربنا إنا لذائقون'),
(3820, 23, 37, 'Aṣ-Ṣāffāt', 'الصَّافَات', 447, 5, 32, '‏‏‏‏‏‏‏‏ ‏‏‏ ‏‏‏ ‏‏‏‏ ‏', 'فأغويناكم إنا كنا غاوين'),
(3821, 23, 37, 'Aṣ-Ṣāffāt', 'الصَّافَات', 447, 6, 33, '‏‏‏‏‏ ‏‏‏‏‏ ‏ ‏‏‏‏‏‏ ‏‏‏‏‏‏‏ ‏', 'فإنهم يومئذ في العذاب مشتركون'),
