(4030, 23, 38, 'Ṣād', 'صٓ', 456, 14, 60, '‏‏‏‏‏ ‏‏ ‏‏‏‏ ‏ ‏‏‏‏‏ ‏‏‏‏ ‏‏‏‏ ‏‏‏‏‏‏‏ ‏‏‏ ‏‏‏‏ ‏‏‏‏‏‏ ‏', 'قالوا بل أنتم لا مرحبا بكم أنتم قدمتموه لنا فبئس القرار'),
(4031, 23, 38, 'Ṣād', 'صٓ', 456, 15, 61, '‏‏‏‏‏ ‏‏‏‏ ‏‏ ‏‏‏ ‏‏ ‏‏‏ ‏‏‏‏ ‏‏‏‏‏ ‏‏‏‏ ‏ ‏‏‏‏ ‏', 'قالوا ربنا من قدم لنا هذا فزده عذابا ضعفا في النار'),
(4032, 23, 38, 'Ṣād', 'صٓ', 457, 1, 62, '‏‏‏‏‏‏ ‏‏ ‏‏ ‏ ‏‏‏ ‏‏‏‏ ‏‏‏ ‏‏‏‏‏ ‏‏ ‏‏‏‏‏‏ ‏', 'وقالوا ما لنا لا نرى رجالا كنا نعدهم من الأشرار'),
(4033, 23, 38, 'Ṣād', 'صٓ', 457, 2, 63, '‏‏‏‏‏‏ ‏‏‏‏‏ ‏‏ ‏‏‏‏ ‏‏‏‏ ‏‏‏‏‏ ‏', 'أتخذناهم سخريا أم زاغت عنهم الأبصار'),
(4034, 23, 38, 'Ṣād', 'صٓ', 457, 3, 64, '‏‏ ‏‏‏ ‏‏ ‏‏‏‏ ‏‏‏ ‏‏‏‏ ‏', 'إن ذلك لحق تخاصم أهل النار'),
(4035, 23, 38, 'Ṣād', 'صٓ', 457, 3, 65, '‏‏ ‏‏‏‏ ‏‏‏ ‏‏‏‏‏ ‏‏‏ ‏‏ ‏‏‏ ‏‏ ‏‏ ‏‏‏‏‏ ‏‏‏‏‏‏ ‏', 'قل إنما أنا منذر وما من إله إلا الله الواحد القهار'),
(4036, 23, 38, 'Ṣād', 'صٓ', 457, 4, 66, '‏‏ ‏‏‏‏‏‏ ‏‏‏‏‏ ‏‏‏ ‏‏‏‏‏‏ ‏‏‏‏‏‏ ‏‏‏‏‏ ‏', 'رب السموات والأرض وما بينهما العزيز الغفار'),
(4037, 23, 38, 'Ṣād', 'صٓ', 457, 5, 67, '‏‏ ‏‏ ‏‏‏‏ ‏‏‏‏ ‏', 'قل هو نبأ عظيم'),
(4038, 23, 38, 'Ṣād', 'صٓ', 457, 5, 68, '‏‏‏‏ ‏‏‏ ‏‏‏‏‏‏ ‏', 'أنتم عنه معرضون'),
(4039, 23, 38, 'Ṣād', 'صٓ', 457, 6, 69, '‏‏ ‏‏ ‏ ‏‏ ‏‏‏ ‏‏‏‏‏ ‏‏‏ ‏‏ ‏‏‏‏‏‏ ‏', 'ما كان لي من علم بالملإ الأعلى إذ يختصمون'),
(4040, 23, 38, 'Ṣād', 'صٓ', 457, 6, 70, '‏‏ ‏‏‏ ‏‏ ‏‏ ‏‏‏‏ ‏‏‏ ‏‏‏‏ ‏‏‏ ‏', 'إن يوحى إلي إلا أنما أنا نذير مبين'),
(4041, 23, 38, 'Ṣād', 'صٓ', 457, 7, 71, '‏‏ ‏‏‏ ‏‏‏ ‏‏‏‏‏‏‏ ‏‏ ‏‏‏ ‏‏‏‏ ‏‏ ‏‏ ‏', 'إذ قال ربك للملائكة إني خالق بشرا من طين'),
(4042, 23, 38, 'Ṣād', 'صٓ', 457, 8, 72, '‏‏‏‏ ‏‏‏‏‏‏ ‏‏‏‏‏ ‏‏‏ ‏‏ ‏‏‏‏ ‏‏‏‏‏ ‏‏ ‏‏‏‏‏ ‏', 'فإذا سويته ونفخت فيه من روحي فقعوا له ساجدين'),
(4043, 23, 38, 'Ṣād', 'صٓ', 457, 9, 73, '‏‏‏‏ ‏‏‏‏‏‏‏ ‏‏‏ ‏‏‏‏‏ ‏', 'فسجد الملائكة كلهم أجمعون'),
(4044, 23, 38, 'Ṣād', 'صٓ', 457, 9, 74, '‏‏ ‏‏‏‏‏ ‏‏‏‏‏‏ ‏‏‏ ‏‏ ‏‏‏‏‏‏‏ ‏', 'إلا إبليس استكبر وكان من الكافرين'),
(4045, 23, 38, 'Ṣād', 'صٓ', 457, 11, 75, '‏‏‏ ‏‏‏‏‏‏ ‏‏ ‏‏‏‏ ‏‏ ‏‏‏‏ ‏‏‏ ‏‏‏‏ ‏‏‏‏‏ ‏‏‏‏‏‏‏ ‏‏ ‏‏‏ ‏‏ ‏‏‏‏‏‏ ‏', 'قال ياإبليس ما منعك أن تسجد لما خلقت بيدي أستكبرت أم كنت من العالين'),
(4046, 23, 38, 'Ṣād', 'صٓ', 457, 11, 76, '‏‏‏ ‏‏‏ ‏‏‏ ‏‏‏ ‏‏‏‏‏ ‏‏ ‏‏‏ ‏‏‏‏‏‏‏ ‏‏ ‏‏ ‏', 'قال أنا خير منه خلقتني من نار وخلقته من طين'),
(4047, 23, 38, 'Ṣād', 'صٓ', 457, 12, 77, '‏‏‏ ‏‏‏‏‏ ‏‏‏‏ ‏‏‏‏ ‏‏‏‏ ‏', 'قال فاخرج منها فإنك رجيم'),
(4048, 23, 38, 'Ṣād', 'صٓ', 457, 12, 78, '‏‏ ‏‏‏‏ ‏‏‏‏ ‏‏ ‏‏‏ ‏‏‏‏ ‏', 'وإن عليك لعنتي إلى يوم الدين'),
(4049, 23, 38, 'Ṣād', 'صٓ', 457, 13, 79, '‏‏‏ ‏‏ ‏‏‏‏‏‏ ‏‏ ‏‏‏ ‏‏‏‏‏‏ ‏', 'قال رب فأنظرني إلى يوم يبعثون'),
(4050, 23, 38, 'Ṣād', 'صٓ', 457, 14, 80, '‏‏‏ ‏‏‏‏ ‏‏ ‏‏‏‏‏‏‏‏ ‏', 'قال فإنك من المنظرين'),
(4051, 23, 38, 'Ṣād', 'صٓ', 457, 14, 81, '‏‏ ‏‏‏ ‏‏‏‏‏ ‏‏‏‏‏‏‏ ‏', 'إلى يوم الوقت المعلوم'),
(4052, 23, 38, 'Ṣād', 'صٓ', 457, 15, 82, '‏‏‏ ‏‏‏‏‏‏ ‏‏‏‏‏‏‏ ‏‏‏‏ ‏', 'قال فبعزتك لأغوينهم أجمعين'),
(4053, 23, 38, 'Ṣād', 'صٓ', 457, 15, 83, '‏‏ ‏‏‏‏‏ ‏‏‏‏ ‏‏‏‏‏‏‏ ‏', 'إلا عبادك منهم المخلصين'),
(4054, 23, 38, 'Ṣād', 'صٓ', 458, 1, 84, '‏‏‏ ‏‏‏‏ ‏‏‏‏ ‏‏‏‏ ‏', 'قال فالحق والحق أقول'),
(4055, 23, 38, 'Ṣād', 'صٓ', 458, 2, 85, '‏‏‏‏ ‏‏‏‏ ‏‏‏ ‏‏‏‏ ‏‏‏‏ ‏‏‏‏ ‏‏‏‏ ‏', 'لأملأن جهنم منك وممن تبعك منهم أجمعين'),
(4056, 23, 38, 'Ṣād', 'صٓ', 458, 2, 86, '‏‏ ‏‏ ‏‏‏‏‏ ‏‏‏‏ ‏‏ ‏‏‏ ‏‏‏ ‏‏‏ ‏‏ ‏‏‏‏‏‏‏ ‏', 'قل ما أسألكم عليه من أجر وما أنا من المتكلفين'),
(4057, 23, 38, 'Ṣād', 'صٓ', 458, 3, 87, '‏‏ ‏‏ ‏‏ ‏‏‏ ‏‏‏‏‏‏ ‏', 'إن هو إلا ذكر للعالمين'),
(4058, 23, 38, 'Ṣād', 'صٓ', 458, 3, 88, '‏‏‏‏‏‏ ‏‏‏‏‏ ‏‏‏ ‏‏‏ ‏', 'ولتعلمن نبأه بعد حين'),
(4059, 23, 39, 'Az-Zumar', 'الزُّمَر', 458, 6, 1, '‏‏‏‏‏ ‏‏‏‏‏ ‏‏ ‏‏ ‏‏‏‏‏‏ ‏‏‏‏‏ ‏', 'تنـزيل الكتاب من الله العزيز الحكيم'),
(4060, 23, 39, 'Az-Zumar', 'الزُّمَر', 458, 7, 2, '‏‏‏ ‏‏‏‏‏ ‏‏‏ ‏‏‏‏‏ ‏‏‏‏ ‏‏‏‏‏ ‏‏ ‏‏‏‏ ‏ ‏‏‏‏ ‏', 'إنا أنـزلنا إليك الكتاب بالحق فاعبد الله مخلصا له الدين'),
(4061, 23, 39, 'Az-Zumar', 'الزُّمَر', 458, 11, 3, '‏‏ ‏ ‏‏‏‏ ‏‏‏‏‏‏ ‏‏‏‏‏ ‏‏‏‏‏ ‏‏ ‏‏‏‏‏ ‏‏‏‏‏ ‏‏ ‏‏‏‏‏‏ ‏‏ ‏‏‏‏‏‏‏ ‏‏ ‏‏ ‏‏‏ ‏‏ ‏‏ ‏‏‏ ‏‏‏‏‏ ‏ ‏‏ ‏‏ ‏‏‏ ‏‏‏‏‏‏‏ ‏‏ ‏‏ ‏ ‏‏‏‏ ‏‏ ‏‏ ‏‏‏ ‏‏‏‏ ‏', 'ألا لله الدين الخالص والذين اتخذوا من دونه أولياء ما نعبدهم إلا ليقربونا إلى الله زلفى إن الله يحكم بينهم في ما هم فيه يختلفون إن الله لا يهدي من هو كاذب كفار'),
(4062, 23, 39, 'Az-Zumar', 'الزُّمَر', 458, 12, 4, '‏‏ ‏‏‏‏ ‏‏ ‏‏ ‏‏‏‏ ‏‏‏ ‏‏‏‏ ‏‏‏ ‏‏‏ ‏‏ ‏‏‏‏‏ ‏‏‏‏‏‏‏ ‏‏ ‏‏ ‏‏‏‏‏ ‏‏‏‏‏‏ ‏', 'لو أراد الله أن يتخذ ولدا لاصطفى مما يخلق ما يشاء سبحانه هو الله الواحد القهار'),
(4063, 23, 39, 'Az-Zumar', 'الزُّمَر', 458, 15, 5, '‏‏‏ ‏‏‏‏‏‏ ‏‏‏‏‏ ‏‏‏‏‏ ‏‏‏‏‏ ‏‏‏ ‏ ‏‏‏‏‏ ‏‏‏‏‏‏ ‏‏‏‏‏ ‏ ‏‏‏‏ ‏‏‏‏ ‏‏‏‏‏ ‏‏‏‏‏‏‏ ‏ ‏‏‏ ‏‏‏ ‏‏‏‏ ‏‏ ‏‏ ‏‏‏‏‏‏ ‏‏‏‏‏ ‏', 'خلق السموات والأرض بالحق يكور الليل على النهار ويكور النهار على الليل وسخر الشمس والقمر كل يجري لأجل مسمى ألا هو العزيز الغفار'),
