(4710, 27, 51, 'Adh-Dhāriyāt', 'الذَّاريَات', 522, 3, 35, '‏‏‏‏‏‏‏ ‏‏ ‏‏ ‏‏‏‏ ‏‏ ‏‏‏‏‏‏‏ ‏', 'فأخرجنا من كان فيها من المؤمنين'),
(4711, 27, 51, 'Adh-Dhāriyāt', 'الذَّاريَات', 522, 4, 36, '‏‏‏ ‏‏‏‏‏ ‏‏‏‏ ‏‏‏ ‏‏‏ ‏‏ ‏‏‏‏‏‏‏ ‏', 'فما وجدنا فيها غير بيت من المسلمين'),
(4712, 27, 51, 'Adh-Dhāriyāt', 'الذَّاريَات', 522, 5, 37, '‏‏‏‏‏‏ ‏‏‏‏ ‏‏‏‏ ‏‏‏‏ ‏‏‏‏‏ ‏‏‏‏‏‏ ‏‏‏‏ ‏', 'وتركنا فيها آية للذين يخافون العذاب الأليم'),
(4713, 27, 51, 'Adh-Dhāriyāt', 'الذَّاريَات', 522, 6, 38, '‏‏ ‏‏‏ ‏‏ ‏‏‏‏‏‏ ‏‏ ‏‏‏‏‏ ‏‏‏‏‏ ‏‏‏ ‏', 'وفي موسى إذ أرسلناه إلى فرعون بسلطان مبين'),
(4714, 27, 51, 'Adh-Dhāriyāt', 'الذَّاريَات', 522, 6, 39, '‏‏‏‏ ‏‏‏‏‏‏ ‏‏‏‏ ‏‏‏ ‏‏ ‏‏‏‏ ‏', 'فتولى بركنه وقال ساحر أو مجنون'),
(4715, 27, 51, 'Adh-Dhāriyāt', 'الذَّاريَات', 522, 7, 40, '‏‏‏‏‏‏ ‏‏‏‏‏‏‏ ‏‏‏‏‏‏‏ ‏ ‏‏‏ ‏‏‏ ‏‏‏‏ ‏', 'فأخذناه وجنوده فنبذناهم في اليم وهو مليم'),
(4716, 27, 51, 'Adh-Dhāriyāt', 'الذَّاريَات', 522, 8, 41, '‏‏ ‏‏ ‏‏ ‏‏‏‏‏‏ ‏‏‏‏‏ ‏‏‏‏‏ ‏‏‏‏‏‏ ‏', 'وفي عاد إذ أرسلنا عليهم الريح العقيم'),
(4717, 27, 51, 'Adh-Dhāriyāt', 'الذَّاريَات', 522, 8, 42, '‏‏ ‏‏‏ ‏‏ ‏‏ ‏‏‏ ‏‏‏‏ ‏‏ ‏‏‏‏‏ ‏‏‏‏‏‏ ‏', 'ما تذر من شيء أتت عليه إلا جعلته كالرميم'),
(4718, 27, 51, 'Adh-Dhāriyāt', 'الذَّاريَات', 522, 9, 43, '‏‏ ‏‏‏‏ ‏‏ ‏‏‏ ‏‏‏ ‏‏‏‏‏‏ ‏‏ ‏‏ ‏', 'وفي ثمود إذ قيل لهم تمتعوا حتى حين'),
(4719, 27, 51, 'Adh-Dhāriyāt', 'الذَّاريَات', 522, 10, 44, '‏‏‏‏‏ ‏‏ ‏‏‏ ‏‏‏‏ ‏‏‏‏‏‏‏ ‏‏‏‏‏‏ ‏‏‏ ‏‏‏‏‏‏‏ ‏', 'فعتوا عن أمر ربهم فأخذتهم الصاعقة وهم ينظرون'),
(4720, 27, 51, 'Adh-Dhāriyāt', 'الذَّاريَات', 522, 11, 45, '‏‏‏ ‏‏‏‏‏‏‏ ‏‏ ‏‏‏‏ ‏‏‏ ‏‏‏‏ ‏‏‏‏‏‏‏ ‏', 'فما استطاعوا من قيام وما كانوا منتصرين'),
(4721, 27, 51, 'Adh-Dhāriyāt', 'الذَّاريَات', 522, 12, 46, '‏‏‏‏ ‏‏‏ ‏‏ ‏‏‏‏ ‏‏‏‏ ‏‏‏‏ ‏‏‏‏ ‏‏‏‏ ‏', 'وقوم نوح من قبل إنهم كانوا قوما فاسقين'),
(4722, 27, 51, 'Adh-Dhāriyāt', 'الذَّاريَات', 522, 12, 47, '‏‏‏‏‏‏‏ ‏‏‏‏‏‏ ‏‏‏‏‏ ‏‏‏ ‏‏‏‏‏‏‏ ‏', 'والسماء بنيناها بأيد وإنا لموسعون'),
(4723, 27, 51, 'Adh-Dhāriyāt', 'الذَّاريَات', 522, 13, 48, '‏‏‏‏‏ ‏‏‏‏‏‏ ‏‏‏‏ ‏‏‏‏‏‏‏ ‏', 'والأرض فرشناها فنعم الماهدون'),
(4724, 27, 51, 'Adh-Dhāriyāt', 'الذَّاريَات', 522, 14, 49, '‏‏‏ ‏ ‏‏ ‏‏‏‏‏ ‏‏‏‏‏ ‏‏‏‏‏ ‏‏‏‏‏‏‏ ‏', 'ومن كل شيء خلقنا زوجين لعلكم تذكرون'),
(4725, 27, 51, 'Adh-Dhāriyāt', 'الذَّاريَات', 522, 14, 50, '‏‏‏‏‏‏ ‏‏ ‏‏‏ ‏‏ ‏‏‏ ‏‏‏ ‏‏‏‏ ‏‏‏ ‏', 'ففروا إلى الله إني لكم منه نذير مبين'),
(4726, 27, 51, 'Adh-Dhāriyāt', 'الذَّاريَات', 522, 15, 51, '‏‏ ‏‏‏‏‏ ‏‏ ‏‏ ‏‏‏‏ ‏‏‏‏‏ ‏‏ ‏‏‏ ‏‏‏ ‏‏‏‏ ‏‏‏ ‏', 'ولا تجعلوا مع الله إلها آخر إني لكم منه نذير مبين'),
(4727, 27, 51, 'Adh-Dhāriyāt', 'الذَّاريَات', 523, 2, 52, '‏‏‏‏ ‏‏ ‏‏ ‏‏‏‏ ‏‏ ‏‏‏‏‏ ‏‏ ‏‏‏‏ ‏‏ ‏‏‏‏‏ ‏‏‏‏ ‏‏ ‏‏‏‏ ‏', 'كذلك ما أتى الذين من قبلهم من رسول إلا قالوا ساحر أو مجنون'),
(4728, 27, 51, 'Adh-Dhāriyāt', 'الذَّاريَات', 523, 2, 53, '‏‏‏‏‏‏‏ ‏‏‏‏ ‏‏ ‏‏ ‏‏‏ ‏‏‏‏‏ ‏', 'أتواصوا به بل هم قوم طاغون'),
(4729, 27, 51, 'Adh-Dhāriyāt', 'الذَّاريَات', 523, 3, 54, '‏‏‏‏ ‏‏‏‏ ‏‏‏ ‏‏‏ ‏‏‏‏‏ ‏', 'فتول عنهم فما أنت بملوم'),
(4730, 27, 51, 'Adh-Dhāriyāt', 'الذَّاريَات', 523, 3, 55, '‏‏‏‏ ‏‏‏ ‏‏‏‏‏ ‏‏‏‏ ‏‏‏‏‏‏‏ ‏', 'وذكر فإن الذكرى تنفع المؤمنين'),
(4731, 27, 51, 'Adh-Dhāriyāt', 'الذَّاريَات', 523, 4, 56, '‏‏‏ ‏‏‏‏ ‏‏‏ ‏‏‏‏‏ ‏‏ ‏‏‏‏‏‏ ‏', 'وما خلقت الجن والإنس إلا ليعبدون'),
(4732, 27, 51, 'Adh-Dhāriyāt', 'الذَّاريَات', 523, 5, 57, '‏‏ ‏‏‏‏ ‏‏‏‏ ‏‏ ‏‏‏‏ ‏‏‏ ‏‏‏‏ ‏‏ ‏‏‏‏‏‏ ‏', 'ما أريد منهم من رزق وما أريد أن يطعمون'),
(4733, 27, 51, 'Adh-Dhāriyāt', 'الذَّاريَات', 523, 5, 58, '‏‏ ‏‏ ‏‏ ‏‏‏‏‏‏‏ ‏‏ ‏‏‏‏‏ ‏‏‏‏‏ ‏', 'إن الله هو الرزاق ذو القوة المتين'),
(4734, 27, 51, 'Adh-Dhāriyāt', 'الذَّاريَات', 523, 7, 59, '‏‏‏ ‏‏‏‏ ‏‏‏‏‏ ‏‏‏‏‏ ‏‏‏ ‏‏‏‏ ‏‏‏‏‏‏ ‏‏ ‏‏‏‏‏‏‏‏ ‏', 'فإن للذين ظلموا ذنوبا مثل ذنوب أصحابهم فلا يستعجلون'),
(4735, 27, 51, 'Adh-Dhāriyāt', 'الذَّاريَات', 523, 7, 60, '‏‏‏‏ ‏‏‏‏ ‏‏‏‏‏‏ ‏‏ ‏‏‏‏‏ ‏‏‏ ‏‏‏‏‏‏ ‏', 'فويل للذين كفروا من يومهم الذي يوعدون'),
(4736, 27, 52, 'Aṭ-Ṭūr', 'الطُّور', 523, 10, 1, '‏‏‏‏‏‏‏ ‏', 'والطور'),
(4737, 27, 52, 'Aṭ-Ṭūr', 'الطُّور', 523, 10, 2, '‏‏‏‏ ‏‏‏‏‏‏ ‏', 'وكتاب مسطور'),
(4738, 27, 52, 'Aṭ-Ṭūr', 'الطُّور', 523, 10, 3, '‏ ‏‏ ‏‏‏‏‏‏ ‏', 'في رق منشور'),
(4739, 27, 52, 'Aṭ-Ṭūr', 'الطُّور', 523, 11, 4, '‏‏‏‏‏ ‏‏‏‏‏‏‏‏ ‏', 'والبيت المعمور'),
(4740, 27, 52, 'Aṭ-Ṭūr', 'الطُّور', 523, 11, 5, '‏‏‏‏‏‏ ‏‏‏‏‏‏‏ ‏', 'والسقف المرفوع'),
(4741, 27, 52, 'Aṭ-Ṭūr', 'الطُّور', 523, 11, 6, '‏‏‏‏‏ ‏‏‏‏‏‏‏‏ ‏', 'والبحر المسجور'),
(4742, 27, 52, 'Aṭ-Ṭūr', 'الطُّور', 523, 12, 7, '‏‏ ‏‏‏‏ ‏‏‏ ‏‏‏‏ ‏', 'إن عذاب ربك لواقع'),
(4743, 27, 52, 'Aṭ-Ṭūr', 'الطُّور', 523, 12, 8, '‏‏ ‏‏ ‏‏ ‏‏‏‏ ‏', 'ما له من دافع'),
(4744, 27, 52, 'Aṭ-Ṭūr', 'الطُّور', 523, 13, 9, '‏‏‏ ‏‏‏‏‏ ‏‏‏‏‏‏ ‏‏‏‏‏ ‏', 'يوم تمور السماء مورا'),
(4745, 27, 52, 'Aṭ-Ṭūr', 'الطُّور', 523, 13, 10, '‏‏‏‏‏ ‏‏‏‏‏ ‏‏‏‏ ‏', 'وتسير الجبال سيرا'),
(4746, 27, 52, 'Aṭ-Ṭūr', 'الطُّور', 523, 13, 11, '‏‏‏‏ ‏‏‏‏‏ ‏‏‏‏‏‏‏ ‏', 'فويل يومئذ للمكذبين'),
(4747, 27, 52, 'Aṭ-Ṭūr', 'الطُّور', 523, 14, 12, '‏‏‏‏ ‏‏ ‏ ‏‏‏ ‏‏‏‏‏‏ ‏', 'الذين هم في خوض يلعبون'),
(4748, 27, 52, 'Aṭ-Ṭūr', 'الطُّور', 523, 15, 13, '‏‏‏ ‏‏‏‏‏ ‏‏ ‏‏‏ ‏‏‏‏ ‏‏ ‏', 'يوم يدعون إلى نار جهنم دعا'),
(4749, 27, 52, 'Aṭ-Ṭūr', 'الطُّور', 523, 15, 14, '‏‏‏ ‏‏‏‏ ‏‏‏ ‏‏‏‏ ‏‏‏ ‏‏‏‏‏‏ ‏', 'هذه النار التي كنتم بها تكذبون'),
(4750, 27, 52, 'Aṭ-Ṭūr', 'الطُّور', 524, 1, 15, '‏‏‏‏‏ ‏‏‏ ‏‏ ‏‏‏‏ ‏ ‏‏‏‏‏‏‏ ‏', 'أفسحر هذا أم أنتم لا تبصرون'),
(4751, 27, 52, 'Aṭ-Ṭūr', 'الطُّور', 524, 2, 16, '‏‏‏‏‏‏ ‏‏‏‏‏‏‏‏ ‏‏ ‏ ‏‏‏‏‏‏‏ ‏‏‏‏ ‏‏‏‏‏‏ ‏‏‏‏ ‏‏‏‏‏ ‏‏ ‏‏‏‏ ‏‏‏‏‏‏ ‏', 'اصلوها فاصبروا أو لا تصبروا سواء عليكم إنما تجزون ما كنتم تعملون'),
(4752, 27, 52, 'Aṭ-Ṭūr', 'الطُّور', 524, 3, 17, '‏‏ ‏‏‏‏‏‏ ‏ ‏‏‏ ‏‏‏‏‏ ‏', 'إن المتقين في جنات ونعيم'),
(4753, 27, 52, 'Aṭ-Ṭūr', 'الطُّور', 524, 4, 18, '‏‏‏‏ ‏‏‏ ‏‏‏‏‏‏ ‏‏‏‏ ‏‏‏‏‏‏‏ ‏‏‏‏ ‏‏‏‏ ‏‏‏‏‏ ‏', 'فاكهين بما آتاهم ربهم ووقاهم ربهم عذاب الجحيم'),
(4754, 27, 52, 'Aṭ-Ṭūr', 'الطُّور', 524, 5, 19, '‏‏‏ ‏‏‏‏‏‏‏ ‏‏‏‏ ‏‏‏ ‏‏‏‏ ‏‏‏‏‏‏ ‏', 'كلوا واشربوا هنيئا بما كنتم تعملون'),
(4755, 27, 52, 'Aṭ-Ṭūr', 'الطُّور', 524, 6, 20, '‏‏‏‏ ‏ ‏‏‏‏ ‏‏‏‏‏‏‏ ‏‏‏‏‏‏‏‏‏ ‏‏‏‏ ‏‏ ‏', 'متكئين على سرر مصفوفة وزوجناهم بحور عين'),
(4756, 27, 52, 'Aṭ-Ṭūr', 'الطُّور', 524, 8, 21, '‏‏‏‏‏ ‏‏‏‏‏‏ ‏‏‏‏‏‏‏‏ ‏‏‏‏‏‏ ‏‏‏‏ ‏‏‏‏‏‏ ‏‏‏ ‏‏‏‏‏‏ ‏‏‏ ‏‏‏‏‏‏ ‏‏ ‏‏‏‏‏ ‏‏ ‏‏‏ ‏ ‏‏‏‏ ‏‏‏ ‏‏‏ ‏‏‏ ‏', 'والذين آمنوا واتبعتهم ذريتهم بإيمان ألحقنا بهم ذريتهم وما ألتناهم من عملهم من شيء كل امرئ بما كسب رهين'),
(4757, 27, 52, 'Aṭ-Ṭūr', 'الطُّور', 524, 8, 22, '‏‏‏‏‏‏‏‏ ‏‏‏‏‏ ‏‏‏ ‏‏‏ ‏‏‏‏‏‏ ‏', 'وأمددناهم بفاكهة ولحم مما يشتهون'),
(4758, 27, 52, 'Aṭ-Ṭūr', 'الطُّور', 524, 9, 23, '‏‏‏‏‏‏‏ ‏‏‏‏ ‏‏‏‏ ‏ ‏‏‏ ‏‏‏‏ ‏‏ ‏‏‏‏‏ ‏', 'يتنازعون فيها كأسا لا لغو فيها ولا تأثيم'),
(4759, 27, 52, 'Aṭ-Ṭūr', 'الطُّور', 524, 10, 24, '‏‏‏‏‏‏ ‏‏‏‏‏ ‏‏‏‏‏ ‏‏‏ ‏‏‏‏‏ ‏‏‏‏ ‏‏‏‏‏ ‏', 'ويطوف عليهم غلمان لهم كأنهم لؤلؤ مكنون'),
(4760, 27, 52, 'Aṭ-Ṭūr', 'الطُّور', 524, 11, 25, '‏‏‏‏‏ ‏‏‏‏‏ ‏ ‏‏‏ ‏‏‏‏‏‏‏‏ ‏', 'وأقبل بعضهم على بعض يتساءلون'),
(4761, 27, 52, 'Aṭ-Ṭūr', 'الطُّور', 524, 11, 26, '‏‏‏‏‏ ‏‏‏ ‏‏‏ ‏‏‏ ‏ ‏‏‏‏‏ ‏‏‏‏‏ ‏', 'قالوا إنا كنا قبل في أهلنا مشفقين'),
(4762, 27, 52, 'Aṭ-Ṭūr', 'الطُّور', 524, 12, 27, '‏‏‏ ‏‏ ‏‏‏‏‏ ‏‏‏‏‏‏‏ ‏‏‏‏ ‏‏‏‏‏‏ ‏', 'فمن الله علينا ووقانا عذاب السموم'),
(4763, 27, 52, 'Aṭ-Ṭūr', 'الطُّور', 524, 13, 28, '‏‏‏ ‏‏‏ ‏‏ ‏‏‏ ‏‏‏‏‏‏ ‏‏‏‏ ‏‏ ‏‏‏‏ ‏‏‏‏‏‏ ‏', 'إنا كنا من قبل ندعوه إنه هو البر الرحيم'),
(4764, 27, 52, 'Aṭ-Ṭūr', 'الطُّور', 524, 14, 29, '‏‏‏‏ ‏‏‏ ‏‏‏ ‏‏‏‏‏ ‏‏‏ ‏‏‏‏ ‏‏ ‏‏‏‏ ‏', 'فذكر فما أنت بنعمة ربك بكاهن ولا مجنون'),
(4765, 27, 52, 'Aṭ-Ṭūr', 'الطُّور', 524, 15, 30, '‏‏ ‏‏‏‏‏‏ ‏‏‏‏ ‏‏‏‏‏ ‏‏‏ ‏‏‏ ‏‏‏‏‏‏ ‏', 'أم يقولون شاعر نتربص به ريب المنون'),
(4766, 27, 52, 'Aṭ-Ṭūr', 'الطُّور', 524, 15, 31, '‏‏ ‏‏‏‏‏‏ ‏‏‏ ‏‏‏‏ ‏‏ ‏‏‏‏‏‏‏‏ ‏', 'قل تربصوا فإني معكم من المتربصين'),
(4767, 27, 52, 'Aṭ-Ṭūr', 'الطُّور', 525, 1, 32, '‏‏ ‏‏‏‏‏‏ ‏‏‏‏‏‏ ‏‏‏‏ ‏‏ ‏‏ ‏‏‏ ‏‏‏‏‏ ‏', 'أم تأمرهم أحلامهم بهذا أم هم قوم طاغون'),
(4768, 27, 52, 'Aṭ-Ṭūr', 'الطُّور', 525, 2, 33, '‏‏ ‏‏‏‏‏‏ ‏‏‏‏‏‏ ‏‏ ‏ ‏‏‏‏‏‏ ‏', 'أم يقولون تقوله بل لا يؤمنون'),
(4769, 27, 52, 'Aṭ-Ṭūr', 'الطُّور', 525, 3, 34, '‏‏‏‏‏‏‏ ‏‏‏‏ ‏‏‏‏‏ ‏‏ ‏‏‏‏ ‏‏‏‏ ‏', 'فليأتوا بحديث مثله إن كانوا صادقين'),
(4770, 27, 52, 'Aṭ-Ṭūr', 'الطُّور', 525, 3, 35, '‏‏ ‏‏‏‏‏ ‏‏ ‏‏‏ ‏‏ ‏‏ ‏‏ ‏‏‏‏‏‏‏ ‏', 'أم خلقوا من غير شيء أم هم الخالقون'),
(4771, 27, 52, 'Aṭ-Ṭūr', 'الطُّور', 525, 4, 36, '‏‏ ‏‏‏‏‏ ‏‏‏‏‏‏ ‏‏‏‏‏‏ ‏‏ ‏ ‏‏‏‏‏‏ ‏', 'أم خلقوا السموات والأرض بل لا يوقنون'),
(4772, 27, 52, 'Aṭ-Ṭūr', 'الطُّور', 525, 5, 37, '‏‏ ‏‏‏‏‏ ‏‏‏‏‏ ‏‏‏ ‏‏ ‏‏ ‏‏‏‏‏‏‏‏‏‏ ‏', 'أم عندهم خزائن ربك أم هم المصيطرون'),
(4773, 27, 52, 'Aṭ-Ṭūr', 'الطُّور', 525, 6, 38, '‏‏ ‏‏‏ ‏‏‏ ‏‏‏‏‏‏‏ ‏‏‏‏ ‏‏‏‏‏ ‏‏‏‏‏‏‏ ‏‏‏‏‏ ‏‏‏ ‏', 'أم لهم سلم يستمعون فيه فليأت مستمعهم بسلطان مبين'),
(4774, 27, 52, 'Aṭ-Ṭūr', 'الطُّور', 525, 7, 39, '‏‏ ‏ ‏‏‏‏ ‏‏‏‏ ‏‏‏‏‏ ‏', 'أم له البنات ولكم البنون'),
(4775, 27, 52, 'Aṭ-Ṭūr', 'الطُّور', 525, 8, 40, '‏‏ ‏‏‏‏‏ ‏‏‏‏ ‏‏‏ ‏‏ ‏‏‏‏ ‏‏‏‏‏‏ ‏', 'أم تسألهم أجرا فهم من مغرم مثقلون'),
(4776, 27, 52, 'Aṭ-Ṭūr', 'الطُّور', 525, 8, 41, '‏‏ ‏‏‏‏‏ ‏‏‏‏‏ ‏‏‏ ‏‏‏‏‏‏ ‏', 'أم عندهم الغيب فهم يكتبون'),
(4777, 27, 52, 'Aṭ-Ṭūr', 'الطُّور', 525, 9, 42, '‏‏ ‏‏‏‏‏‏ ‏‏‏‏‏ ‏‏‏‏‏ ‏‏‏‏‏‏ ‏‏ ‏‏‏‏‏‏‏‏ ‏', 'أم يريدون كيدا فالذين كفروا هم المكيدون'),
(4778, 27, 52, 'Aṭ-Ṭūr', 'الطُّور', 525, 10, 43, '‏‏ ‏‏‏ ‏‏‏ ‏‏‏ ‏‏‏ ‏‏‏‏ ‏‏ ‏‏‏ ‏‏‏‏‏‏ ‏', 'أم لهم إله غير الله سبحان الله عما يشركون'),
(4779, 27, 52, 'Aṭ-Ṭūr', 'الطُّور', 525, 11, 44, '‏‏ ‏‏‏‏‏ ‏‏‏‏ ‏‏ ‏‏‏‏‏‏ ‏‏‏‏‏ ‏‏‏‏‏‏ ‏‏‏‏ ‏‏‏‏‏ ‏', 'وإن يروا كسفا من السماء ساقطا يقولوا سحاب مركوم'),
(4780, 27, 52, 'Aṭ-Ṭūr', 'الطُّور', 525, 12, 45, '‏‏‏‏‏ ‏‏ ‏‏‏‏‏ ‏‏‏‏‏ ‏‏‏ ‏‏‏ ‏‏‏‏‏‏ ‏', 'فذرهم حتى يلاقوا يومهم الذي فيه يصعقون'),
(4781, 27, 52, 'Aṭ-Ṭūr', 'الطُّور', 525, 12, 46, '‏‏‏ ‏ ‏‏‏ ‏‏‏‏ ‏‏‏‏‏ ‏‏‏ ‏‏ ‏‏ ‏‏‏‏‏‏‏ ‏', 'يوم لا يغني عنهم كيدهم شيئا ولا هم ينصرون'),
(4782, 27, 52, 'Aṭ-Ṭūr', 'الطُّور', 525, 14, 47, '‏‏ ‏‏‏‏ ‏‏‏‏‏ ‏‏‏‏‏ ‏‏‏ ‏‏‏ ‏‏‏‏ ‏‏‏‏‏‏ ‏ ‏‏‏‏‏‏ ‏', 'وإن للذين ظلموا عذابا دون ذلك ولكن أكثرهم لا يعلمون'),
(4783, 27, 52, 'Aṭ-Ṭūr', 'الطُّور', 525, 15, 48, '‏‏‏‏‏ ‏‏‏ ‏‏‏ ‏‏‏‏ ‏‏‏‏‏‏‏‏ ‏‏‏‏ ‏‏‏ ‏‏‏ ‏‏ ‏‏‏‏ ‏', 'واصبر لحكم ربك فإنك بأعيننا وسبح بحمد ربك حين تقوم'),
(4784, 27, 52, 'Aṭ-Ṭūr', 'الطُّور', 525, 15, 49, '‏‏‏ ‏‏‏ ‏‏‏‏‏ ‏‏‏‏ ‏‏‏‏‏ ‏', 'ومن الليل فسبحه وإدبار النجوم'),
(4785, 27, 53, 'An-Najm', 'النَّجم', 526, 3, 1, '‏‏‏‏‏ ‏‏‏ ‏‏‏ ‏', 'والنجم إذا هوى'),
(4786, 27, 53, 'An-Najm', 'النَّجم', 526, 3, 2, '‏‏ ‏‏ ‏‏‏‏‏‏ ‏‏‏ ‏‏‏ ‏', 'ما ضل صاحبكم وما غوى'),
(4787, 27, 53, 'An-Najm', 'النَّجم', 526, 4, 3, '‏‏‏ ‏‏‏‏ ‏‏ ‏‏‏‏‏ ‏', 'وما ينطق عن الهوى'),
(4788, 27, 53, 'An-Najm', 'النَّجم', 526, 4, 4, '‏‏ ‏‏ ‏‏ ‏‏ ‏‏‏ ‏', 'إن هو إلا وحي يوحى'),
(4789, 27, 53, 'An-Najm', 'النَّجم', 526, 4, 5, '‏‏‏‏‏ ‏‏‏‏ ‏‏‏‏‏ ‏', 'علمه شديد القوى'),
(4790, 27, 53, 'An-Najm', 'النَّجم', 526, 5, 6, '‏‏ ‏‏‏ ‏‏‏‏‏‏ ‏', 'ذو مرة فاستوى'),
(4791, 27, 53, 'An-Najm', 'النَّجم', 526, 5, 7, '‏‏‏ ‏‏‏‏‏ ‏‏‏ ‏', 'وهو بالأفق الأعلى'),
(4792, 27, 53, 'An-Najm', 'النَّجم', 526, 5, 8, '‏‏ ‏‏‏ ‏‏‏‏ ‏', 'ثم دنا فتدلى'),
(4793, 27, 53, 'An-Najm', 'النَّجم', 526, 6, 9, '‏‏‏ ‏‏‏ ‏‏‏‏ ‏‏ ‏‏‏ ‏', 'فكان قاب قوسين أو أدنى'),
(4794, 27, 53, 'An-Najm', 'النَّجم', 526, 6, 10, '‏‏‏‏ ‏‏ ‏‏‏‏‏ ‏‏ ‏‏‏ ‏', 'فأوحى إلى عبده ما أوحى'),
(4795, 27, 53, 'An-Najm', 'النَّجم', 526, 7, 11, '‏‏ ‏‏‏ ‏‏‏‏‏‏ ‏‏ ‏‏‏ ‏', 'ما كذب الفؤاد ما رأى'),
(4796, 27, 53, 'An-Najm', 'النَّجم', 526, 7, 12, '‏‏‏‏‏‏‏‏‏‏ ‏ ‏‏ ‏‏‏ ‏', 'أفتمارونه على ما يرى'),
(4797, 27, 53, 'An-Najm', 'النَّجم', 526, 7, 13, '‏‏‏‏ ‏‏‏‏ ‏‏‏‏ ‏‏‏‏ ‏', 'ولقد رآه نـزلة أخرى'),
(4798, 27, 53, 'An-Najm', 'النَّجم', 526, 8, 14, '‏‏‏ ‏‏‏‏ ‏‏‏‏‏‏ ‏', 'عند سدرة المنتهى'),
(4799, 27, 53, 'An-Najm', 'النَّجم', 526, 8, 15, '‏‏‏‏‏ ‏‏‏ ‏‏‏‏‏‏ ‏', 'عندها جنة المأوى'),
(4800, 27, 53, 'An-Najm', 'النَّجم', 526, 9, 16, '‏‏ ‏‏‏ ‏‏‏‏‏‏ ‏‏ ‏‏‏ ‏', 'إذ يغشى السدرة ما يغشى'),
(4801, 27, 53, 'An-Najm', 'النَّجم', 526, 9, 17, '‏‏ ‏‏‏ ‏‏‏‏ ‏‏‏ ‏‏ ‏', 'ما زاغ البصر وما طغى'),
(4802, 27, 53, 'An-Najm', 'النَّجم', 526, 10, 18, '‏‏‏ ‏‏‏ ‏‏ ‏‏‏‏ ‏‏‏ ‏‏‏‏‏‏ ‏', 'لقد رأى من آيات ربه الكبرى'),
(4803, 27, 53, 'An-Najm', 'النَّجم', 526, 10, 19, '‏‏‏‏‏‏‏ ‏‏‏‏ ‏‏‏‏‏‏ ‏', 'أفرأيتم اللات والعزى'),
(4804, 27, 53, 'An-Najm', 'النَّجم', 526, 10, 20, '‏‏‏‏‏ ‏‏‏‏‏ ‏‏‏‏‏ ‏', 'ومناة الثالثة الأخرى'),
(4805, 27, 53, 'An-Najm', 'النَّجم', 526, 11, 21, '‏‏‏‏ ‏‏‏‏ ‏‏ ‏‏‏‏ ‏', 'ألكم الذكر وله الأنثى'),
(4806, 27, 53, 'An-Najm', 'النَّجم', 526, 11, 22, '‏‏‏ ‏‏‏ ‏‏‏‏ ‏‏‏‏ ‏', 'تلك إذا قسمة ضيزى'),
(4807, 27, 53, 'An-Najm', 'النَّجم', 526, 13, 23, '‏‏ ‏ ‏‏ ‏‏‏‏‏ ‏‏‏‏‏‏‏‏ ‏‏‏‏ ‏‏‏‏‏‏‏‏ ‏‏ ‏‏‏‏ ‏‏ ‏‏‏ ‏‏ ‏‏‏‏‏ ‏‏ ‏‏‏‏‏‏ ‏‏ ‏‏‏‏ ‏‏‏ ‏‏‏‏ ‏‏‏‏‏‏ ‏‏‏‏ ‏‏‏‏‏ ‏‏ ‏‏‏‏ ‏‏‏‏‏ ‏', 'إن هي إلا أسماء سميتموها أنتم وآباؤكم ما أنـزل الله بها من سلطان إن يتبعون إلا الظن وما تهوى الأنفس ولقد جاءهم من ربهم الهدى'),
(4808, 27, 53, 'An-Najm', 'النَّجم', 526, 14, 24, '‏‏ ‏‏‏‏‏ ‏‏ ‏‏‏ ‏', 'أم للإنسان ما تمنى'),
(4809, 27, 53, 'An-Najm', 'النَّجم', 526, 14, 25, '‏‏‏‏ ‏‏‏‏‏ ‏‏‏‏‏ ‏', 'فلله الآخرة والأولى'),
(4810, 27, 53, 'An-Najm', 'النَّجم', 526, 15, 26, '‏‏‏‏ ‏‏ ‏‏‏ ‏ ‏‏‏‏‏‏ ‏ ‏‏‏ ‏‏‏‏‏‏ ‏‏‏ ‏‏ ‏‏ ‏‏‏ ‏‏ ‏‏‏‏ ‏‏ ‏‏‏ ‏‏‏‏ ‏‏‏‏ ‏', 'وكم من ملك في السموات لا تغني شفاعتهم شيئا إلا من بعد أن يأذن الله لمن يشاء ويرضى'),
(4811, 27, 53, 'An-Najm', 'النَّجم', 527, 1, 27, '‏‏ ‏‏‏‏ ‏ ‏‏‏‏‏‏ ‏‏‏‏‏‏ ‏‏‏‏‏ ‏‏‏‏‏‏‏ ‏‏‏‏‏ ‏‏‏‏ ‏', 'إن الذين لا يؤمنون بالآخرة ليسمون الملائكة تسمية الأنثى'),
(4812, 27, 53, 'An-Najm', 'النَّجم', 527, 3, 28, '‏‏‏ ‏‏‏ ‏‏‏ ‏‏ ‏‏‏‏ ‏‏ ‏‏‏‏‏‏ ‏‏ ‏‏‏‏‏ ‏‏ ‏‏‏‏ ‏ ‏‏‏ ‏‏ ‏‏‏ ‏‏‏ ‏', 'وما لهم به من علم إن يتبعون إلا الظن وإن الظن لا يغني من الحق شيئا'),
(4813, 27, 53, 'An-Najm', 'النَّجم', 527, 4, 29, '‏‏‏‏‏ ‏‏ ‏‏ ‏‏‏ ‏‏ ‏‏‏‏‏ ‏‏‏ ‏‏‏ ‏‏ ‏‏‏‏‏ ‏‏‏‏‏ ‏', 'فأعرض عن من تولى عن ذكرنا ولم يرد إلا الحياة الدنيا'),
(4814, 27, 53, 'An-Najm', 'النَّجم', 527, 5, 30, '‏‏‏ ‏‏‏‏‏‏ ‏‏ ‏‏‏‏‏‏ ‏‏ ‏‏‏ ‏‏ ‏‏‏‏ ‏‏‏ ‏‏ ‏‏ ‏‏‏‏‏‏ ‏‏‏ ‏‏‏‏ ‏‏‏ ‏‏‏‏‏ ‏', 'ذلك مبلغهم من العلم إن ربك هو أعلم بمن ضل عن سبيله وهو أعلم بمن اهتدى'),
(4815, 27, 53, 'An-Najm', 'النَّجم', 527, 7, 31, '‏‏ ‏‏ ‏ ‏‏‏‏‏‏ ‏‏‏ ‏ ‏‏‏‏ ‏‏‏‏ ‏‏‏‏ ‏‏‏‏‏ ‏‏‏ ‏‏‏‏‏ ‏‏‏‏ ‏‏‏‏ ‏‏‏‏‏‏ ‏‏‏‏‏ ‏', 'ولله ما في السموات وما في الأرض ليجزي الذين أساءوا بما عملوا ويجزي الذين أحسنوا بالحسنى');
INSERT INTO `sour` (`id`, `jozz`, `sura_no`, `sura_name_en`, `sura_name_ar`, `page`, `line`, `aya_no`, `aya_text`, `aya_text_emlaey`) VALUES
(4816, 27, 53, 'An-Najm', 'النَّجم', 527, 10, 32, '‏‏‏‏ ‏‏‏‏‏‏ ‏‏‏‏ ‏‏‏‏ ‏‏‏‏‏‏‏ ‏‏ ‏‏‏‏‏‏ ‏‏ ‏‏‏ ‏‏‏ ‏‏‏‏‏‏‏‏ ‏‏ ‏‏‏‏ ‏‏‏ ‏‏ ‏‏‏‏‏‏ ‏‏ ‏‏‏‏ ‏‏ ‏‏‏‏ ‏‏‏‏ ‏ ‏‏‏‏ ‏‏‏‏‏‏‏ ‏‏ ‏‏‏‏‏ ‏‏‏‏‏‏‏ ‏‏ ‏‏‏‏ ‏‏‏ ‏‏‏ ‏', 'الذين يجتنبون كبائر الإثم والفواحش إلا اللمم إن ربك واسع المغفرة هو أعلم بكم إذ أنشأكم من الأرض وإذ أنتم أجنة في بطون أمهاتكم فلا تزكوا أنفسكم هو أعلم بمن اتقى'),
(4817, 27, 53, 'An-Najm', 'النَّجم', 527, 10, 33, '‏‏‏‏‏‏ ‏‏‏ ‏‏‏ ‏', 'أفرأيت الذي تولى'),
(4818, 27, 53, 'An-Najm', 'النَّجم', 527, 10, 34, '‏‏‏‏ ‏‏‏‏ ‏‏‏‏‏ ‏', 'وأعطى قليلا وأكدى'),
(4819, 27, 53, 'An-Najm', 'النَّجم', 527, 11, 35, '‏‏‏‏‏‏ ‏‏‏ ‏‏‏‏‏ ‏‏‏ ‏‏‏ ‏', 'أعنده علم الغيب فهو يرى'),
(4820, 27, 53, 'An-Najm', 'النَّجم', 527, 12, 36, '‏‏ ‏‏ ‏‏‏‏ ‏‏‏ ‏ ‏‏‏ ‏‏‏ ‏', 'أم لم ينبأ بما في صحف موسى'),
(4821, 27, 53, 'An-Najm', 'النَّجم', 527, 12, 37, '‏‏‏‏‏‏ ‏‏‏ ‏‏ ‏', 'وإبراهيم الذي وفى'),
(4822, 27, 53, 'An-Najm', 'النَّجم', 527, 12, 38, '‏‏ ‏‏‏‏ ‏‏‏‏‏‏ ‏‏‏‏‏ ‏‏‏‏ ‏', 'ألا تزر وازرة وزر أخرى'),
(4823, 27, 53, 'An-Najm', 'النَّجم', 527, 13, 39, '‏‏‏ ‏‏ ‏‏‏‏‏ ‏‏ ‏‏ ‏‏ ‏', 'وأن ليس للإنسان إلا ما سعى'),
(4824, 27, 53, 'An-Najm', 'النَّجم', 527, 13, 40, '‏‏‏ ‏‏‏‏‏ ‏‏‏ ‏‏‏ ‏', 'وأن سعيه سوف يرى'),
(4825, 27, 53, 'An-Najm', 'النَّجم', 527, 14, 41, '‏‏ ‏‏‏‏ ‏‏‏‏‏ ‏‏‏‏ ‏', 'ثم يجزاه الجزاء الأوفى'),
(4826, 27, 53, 'An-Najm', 'النَّجم', 527, 14, 42, '‏‏‏ ‏‏ ‏‏‏ ‏‏‏‏‏‏ ‏', 'وأن إلى ربك المنتهى'),
