(5285, 29, 68, 'Al-Qalam', 'القَلَم', 564, 14, 14, '‏‏ ‏‏ ‏‏ ‏‏‏ ‏‏‏‏ ‏', 'أن كان ذا مال وبنين'),
(5286, 29, 68, 'Al-Qalam', 'القَلَم', 564, 15, 15, '‏‏‏ ‏‏‏ ‏‏‏‏ ‏‏‏‏‏‏ ‏‏‏ ‏‏‏‏‏ ‏‏‏‏‏ ‏', 'إذا تتلى عليه آياتنا قال أساطير الأولين'),
(5287, 29, 68, 'Al-Qalam', 'القَلَم', 564, 15, 16, '‏‏‏‏‏‏ ‏ ‏‏‏‏‏‏ ‏', 'سنسمه على الخرطوم'),
(5288, 29, 68, 'Al-Qalam', 'القَلَم', 565, 1, 17, '‏‏‏ ‏‏‏‏‏‏ ‏‏‏ ‏‏‏‏‏ ‏‏‏‏ ‏‏‏‏ ‏‏ ‏‏‏‏‏‏ ‏‏‏‏‏‏‏ ‏‏‏‏‏ ‏', 'إنا بلوناهم كما بلونا أصحاب الجنة إذ أقسموا ليصرمنها مصبحين'),
(5289, 29, 68, 'Al-Qalam', 'القَلَم', 565, 2, 18, '‏‏ ‏‏‏‏‏‏‏ ‏', 'ولا يستثنون'),
(5290, 29, 68, 'Al-Qalam', 'القَلَم', 565, 2, 19, '‏‏‏‏ ‏‏‏‏‏ ‏‏‏‏ ‏‏ ‏‏‏ ‏‏‏ ‏‏‏‏‏‏ ‏', 'فطاف عليها طائف من ربك وهم نائمون'),
(5291, 29, 68, 'Al-Qalam', 'القَلَم', 565, 3, 20, '‏‏‏‏‏‏ ‏‏‏‏‏‏ ‏', 'فأصبحت كالصريم'),
(5292, 29, 68, 'Al-Qalam', 'القَلَم', 565, 3, 21, '‏‏‏‏‏‏‏ ‏‏‏‏‏ ‏', 'فتنادوا مصبحين'),
(5293, 29, 68, 'Al-Qalam', 'القَلَم', 565, 4, 22, '‏‏ ‏‏‏‏‏ ‏ ‏‏‏‏‏ ‏‏ ‏‏‏‏ ‏‏‏‏ ‏', 'أن اغدوا على حرثكم إن كنتم صارمين'),
(5294, 29, 68, 'Al-Qalam', 'القَلَم', 565, 4, 23, '‏‏‏‏‏‏‏‏ ‏‏‏ ‏‏‏‏‏‏‏ ‏', 'فانطلقوا وهم يتخافتون'),
(5295, 29, 68, 'Al-Qalam', 'القَلَم', 565, 5, 24, '‏‏ ‏ ‏‏‏‏‏‏‏ ‏‏‏‏ ‏‏‏‏‏ ‏‏‏‏ ‏', 'أن لا يدخلنها اليوم عليكم مسكين'),
(5296, 29, 68, 'Al-Qalam', 'القَلَم', 565, 5, 25, '‏‏‏‏‏ ‏ ‏‏‏ ‏‏‏‏‏ ‏', 'وغدوا على حرد قادرين');
