(5297, 29, 68, 'Al-Qalam', 'القَلَم', 565, 6, 26, '‏‏‏‏ ‏‏‏‏‏ ‏‏‏‏‏ ‏‏‏ ‏‏‏‏‏‏ ‏', 'فلما رأوها قالوا إنا لضالون'),
(5298, 29, 68, 'Al-Qalam', 'القَلَم', 565, 6, 27, '‏‏ ‏‏ ‏‏‏‏‏‏‏ ‏', 'بل نحن محرومون'),
(5299, 29, 68, 'Al-Qalam', 'القَلَم', 565, 7, 28, '‏‏‏ ‏‏‏‏‏‏ ‏‏‏ ‏‏‏ ‏‏‏ ‏‏‏ ‏‏‏‏‏‏ ‏', 'قال أوسطهم ألم أقل لكم لولا تسبحون'),
(5300, 29, 68, 'Al-Qalam', 'القَلَم', 565, 7, 29, '‏‏‏‏‏ ‏‏‏‏ ‏‏‏‏ ‏‏‏ ‏‏‏ ‏‏‏‏ ‏', 'قالوا سبحان ربنا إنا كنا ظالمين'),
(5301, 29, 68, 'Al-Qalam', 'القَلَم', 565, 8, 30, '‏‏‏‏‏ ‏‏‏‏‏ ‏ ‏‏‏ ‏‏‏‏‏‏‏ ‏', 'فأقبل بعضهم على بعض يتلاومون'),
(5302, 29, 68, 'Al-Qalam', 'القَلَم', 565, 8, 31, '‏‏‏‏‏ ‏‏‏‏‏‏ ‏‏‏ ‏‏‏ ‏‏‏ ‏', 'قالوا ياويلنا إنا كنا طاغين'),
(5303, 29, 68, 'Al-Qalam', 'القَلَم', 565, 9, 32, '‏‏ ‏‏‏‏ ‏‏ ‏‏‏‏‏ ‏‏‏‏ ‏‏‏‏ ‏‏‏ ‏‏ ‏‏‏‏ ‏‏‏‏‏ ‏', 'عسى ربنا أن يبدلنا خيرا منها إنا إلى ربنا راغبون'),
(5304, 29, 68, 'Al-Qalam', 'القَلَم', 565, 10, 33, '‏‏‏‏ ‏‏‏‏‏‏‏ ‏‏‏‏‏‏ ‏‏‏‏‏ ‏‏‏‏‏ ‏‏ ‏‏‏‏ ‏‏‏‏‏‏ ‏', 'كذلك العذاب ولعذاب الآخرة أكبر لو كانوا يعلمون'),
(5305, 29, 68, 'Al-Qalam', 'القَلَم', 565, 10, 34, '‏‏ ‏‏‏‏‏‏ ‏‏‏ ‏‏‏‏ ‏‏‏ ‏‏‏‏‏ ‏', 'إن للمتقين عند ربهم جنات النعيم'),
(5306, 29, 68, 'Al-Qalam', 'القَلَم', 565, 11, 35, '‏‏‏‏‏‏ ‏‏‏‏‏‏‏ ‏‏‏‏‏‏‏ ‏', 'أفنجعل المسلمين كالمجرمين'),
(5307, 29, 68, 'Al-Qalam', 'القَلَم', 565, 11, 36, '‏‏ ‏‏‏ ‏‏‏ ‏‏‏‏‏ ‏', 'ما لكم كيف تحكمون'),
(5308, 29, 68, 'Al-Qalam', 'القَلَم', 565, 12, 37, '‏‏ ‏‏‏ ‏‏‏ ‏‏‏ ‏‏‏‏‏‏ ‏', 'أم لكم كتاب فيه تدرسون'),
(5309, 29, 68, 'Al-Qalam', 'القَلَم', 565, 12, 38, '‏‏ ‏‏‏ ‏‏‏ ‏‏‏ ‏‏‏‏‏‏ ‏', 'إن لكم فيه لما تخيرون'),
(5310, 29, 68, 'Al-Qalam', 'القَلَم', 565, 13, 39, '‏‏ ‏‏‏ ‏‏‏‏ ‏‏‏‏‏ ‏‏‏‏ ‏‏ ‏‏‏ ‏‏‏‏‏‏ ‏‏ ‏‏‏ ‏‏‏ ‏‏‏‏‏ ‏', 'أم لكم أيمان علينا بالغة إلى يوم القيامة إن لكم لما تحكمون'),
(5311, 29, 68, 'Al-Qalam', 'القَلَم', 565, 14, 40, '‏‏‏‏ ‏‏‏‏ ‏‏‏‏ ‏‏‏‏ ‏', 'سلهم أيهم بذلك زعيم'),
(5312, 29, 68, 'Al-Qalam', 'القَلَم', 565, 14, 41, '‏‏ ‏‏‏ ‏‏‏‏ ‏‏‏‏‏‏‏ ‏‏‏‏‏‏‏ ‏‏ ‏‏‏‏ ‏‏‏‏ ‏', 'أم لهم شركاء فليأتوا بشركائهم إن كانوا صادقين'),
(5313, 29, 68, 'Al-Qalam', 'القَلَم', 565, 15, 42, '‏‏‏ ‏‏‏‏ ‏‏ ‏‏‏ ‏‏‏‏‏‏ ‏‏ ‏‏‏‏‏‏ ‏‏ ‏‏‏‏‏‏‏‏ ‏', 'يوم يكشف عن ساق ويدعون إلى السجود فلا يستطيعون'),
(5314, 29, 68, 'Al-Qalam', 'القَلَم', 566, 2, 43, '‏‏‏‏ ‏‏‏‏‏‏ ‏‏‏‏‏‏ ‏‏‏ ‏‏‏ ‏‏‏‏ ‏‏‏‏‏ ‏‏ ‏‏‏‏‏‏ ‏‏‏ ‏‏‏‏‏ ‏', 'خاشعة أبصارهم ترهقهم ذلة وقد كانوا يدعون إلى السجود وهم سالمون'),
(5315, 29, 68, 'Al-Qalam', 'القَلَم', 566, 3, 44, '‏‏‏‏ ‏‏‏ ‏‏‏‏ ‏‏‏‏ ‏‏‏‏‏‏ ‏‏‏‏‏‏‏‏‏ ‏‏ ‏‏‏ ‏ ‏‏‏‏‏‏ ‏', 'فذرني ومن يكذب بهذا الحديث سنستدرجهم من حيث لا يعلمون'),
(5316, 29, 68, 'Al-Qalam', 'القَلَم', 566, 3, 45, '‏‏‏‏ ‏‏‏‏ ‏‏ ‏‏‏‏ ‏‏‏ ‏', 'وأملي لهم إن كيدي متين'),
(5317, 29, 68, 'Al-Qalam', 'القَلَم', 566, 4, 46, '‏‏ ‏‏‏‏‏ ‏‏‏‏ ‏‏‏ ‏‏ ‏‏‏‏ ‏‏‏‏‏‏ ‏', 'أم تسألهم أجرا فهم من مغرم مثقلون'),
(5318, 29, 68, 'Al-Qalam', 'القَلَم', 566, 4, 47, '‏‏ ‏‏‏‏‏ ‏‏‏‏‏ ‏‏‏ ‏‏‏‏‏‏ ‏', 'أم عندهم الغيب فهم يكتبون'),
(5319, 29, 68, 'Al-Qalam', 'القَلَم', 566, 6, 48, '‏‏‏‏‏ ‏‏‏ ‏‏‏ ‏‏ ‏‏‏ ‏‏‏‏‏ ‏‏‏‏ ‏‏ ‏‏‏‏ ‏‏‏ ‏‏‏‏‏ ‏', 'فاصبر لحكم ربك ولا تكن كصاحب الحوت إذ نادى وهو مكظوم'),
(5320, 29, 68, 'Al-Qalam', 'القَلَم', 566, 7, 49, '‏‏‏ ‏‏ ‏‏‏‏‏‏ ‏‏‏‏ ‏‏ ‏‏‏‏ ‏‏‏ ‏‏‏‏‏‏‏ ‏‏‏ ‏‏‏‏‏ ‏', 'لولا أن تداركه نعمة من ربه لنبذ بالعراء وهو مذموم'),
(5321, 29, 68, 'Al-Qalam', 'القَلَم', 566, 7, 50, '‏‏‏‏‏‏ ‏‏‏‏ ‏‏‏‏‏‏ ‏‏ ‏‏‏‏‏‏ ‏', 'فاجتباه ربه فجعله من الصالحين'),
(5322, 29, 68, 'Al-Qalam', 'القَلَم', 566, 9, 51, '‏‏ ‏‏‏ ‏‏‏‏ ‏‏‏‏‏‏ ‏‏‏‏‏‏‏ ‏‏‏‏‏‏‏ ‏‏‏ ‏‏‏‏‏ ‏‏‏‏ ‏‏‏‏‏‏‏ ‏‏‏‏ ‏‏‏‏‏‏ ‏', 'وإن يكاد الذين كفروا ليزلقونك بأبصارهم لما سمعوا الذكر ويقولون إنه لمجنون'),
(5323, 29, 68, 'Al-Qalam', 'القَلَم', 566, 9, 52, '‏‏‏ ‏‏ ‏‏ ‏‏‏ ‏‏‏‏‏‏ ‏', 'وما هو إلا ذكر للعالمين'),
